// client/src/App.js (Aktualisierter Code)

import React from 'react';
import { Routes, Route, Link } from 'react-router-dom';
// Bitte stellen Sie sicher, dass logo.jpg in client/src/assets/ liegt
import logo from './assets/logo.jpg'; 
// !!! Der Banner-Import ist jetzt nicht mehr n√∂tig, da er im CSS als Hintergrund ist !!!
// import banner from './assets/banner.jpg'; 

// --- Platzhalter-Komponenten f√ºr die Seiten ---
// (Bleiben unver√§ndert)
const HomePage = () => (
  <div style={{ padding: '20px', textAlign: 'center' }}>
    <img 
      src={logo} 
      alt="Henkes-Stoffzauber Logo" 
      style={{ width: '150px', margin: '20px 0' }}
    />
    <h2>Willkommen im N√§h-Paradies!</h2>
    <p>Hier finden Sie die neuesten Schnitte, Stoffe und Zubeh√∂r f√ºr Ihre M√ºtzen, Schals und Taschen. Starten Sie jetzt Ihre Kreativit√§t!</p>

    <Link to="/shop" className="nav-button">Zum Shop</Link>
    <Link to="/blog" className="nav-button" style={{ marginLeft: '10px' }}>Zum Blog</Link>
  </div>
);

const ShopPage = () => (
  <div style={{ padding: '20px' }}>
    <h2>üõçÔ∏è Der Stoffzauber-Shop</h2>
    <p>Hier kommen bald die Produkte aus der Node.js/MongoDB-Datenbank.</p>
  </div>
);

const BlogPage = () => (
  <div style={{ padding: '20px' }}>
    <h2>‚úÇÔ∏è Andreas N√§htipps & Blog</h2>
    <p>Neueste Anleitungen und Inspirationen f√ºr kreative N√§hprojekte.</p>
  </div>
);

// --- Haupt-App-Layout ---
function App() {
  return (
    <div className="App"> {/* .App Container ist jetzt wichtig f√ºr Hintergrund-Overlay */}

      {/* 1. Navigationsleiste (JETZT OBEN FIXIERT) */}
      <nav>
        <Link to="/">Home</Link>
        <Link to="/shop">Shop</Link>
        <Link to="/blog">Blog</Link>
      </nav>

      {/* 2. Haupt-Inhaltsbereich (main) */}
      <main>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/shop" element={<ShopPage />} />
          <Route path="/blog" element={<BlogPage />} />
          <Route path="*" element={<h2 style={{padding: '20px'}}>404 - Seite nicht gefunden</h2>} />
        </Routes>
      </main>

      {/* 3. Footer */}
      <footer className="App-footer">
        ¬© 2025 Henkes-Stoffzauber.de
      </footer>
    </div>
  );
}

export default App;
