import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from './CartContext';
import './Shop.css';

const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/CCCCCC/333333?text=Stoffmuster";

const getImageUrl = (imagePath) => imagePath ? `${BACKEND_URL}${imagePath}` : FALLBACK_IMAGE_URL;

const ProductCard = ({ product, onAdd }) => (
  <div className="product-card">
    <Link to={`/shop/${product.id}`}>
      <img src={getImageUrl(product.imageUrl)} alt={product.name} />
    </Link>
    <h3>{product.name}</h3>
    <p>‚Ç¨ {product.price.toFixed(2)}</p>
    <p className={product.stock > 0 ? 'instock' : 'outstock'}>
      {product.stock > 0 ? 'Sofort verf√ºgbar' : 'Ausverkauft'}
    </p>
    <button onClick={() => onAdd(product)} disabled={product.stock <= 0}>
      {product.stock > 0 ? 'In den Warenkorb' : 'Nicht lieferbar'}
    </button>
  </div>
);

const Shop = () => {
  const { cartItems, addToCart, removeFromCart } = useCart();
  const [products, setProducts] = useState([]);
  const [cartVisible, setCartVisible] = useState(false);

  useEffect(() => {
    fetch(API_URL)
      .then(res => res.json())
      .then(setProducts)
      .catch(console.error);
  }, []);

  return (
    <div className="shop-container">
      <h1>Shop</h1>
      <div className="product-grid">
        {products.map(p => <ProductCard key={p.id} product={p} onAdd={addToCart} />)}
      </div>

      {/* Floating Button */}
      <button className="open-cart-button" onClick={() => setCartVisible(v => !v)}>
        üõí {cartItems.reduce((sum, item) => sum + item.quantity, 0)}
      </button>

      {/* Cart Panel ‚Äì always mounted */}
      <div className={`sticky-cart ${cartVisible ? 'visible' : ''}`}>
        <h3>üõí Dein Warenkorb</h3>
        {cartItems.length === 0 ? (
          <p>Dein Warenkorb ist leer</p>
        ) : (
          <ul>
            {cartItems.map(item => (
              <li key={item.id}>
                {item.name} x {item.quantity}
                <button onClick={() => removeFromCart(item.id)}>‚ùå</button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Shop;
