// client/src/components/ProductForm.js
import React, { useState, useEffect, useMemo } from 'react';

// Konstanten für Backend-URLs
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/100x100/CCCCCC/333?text=Bild";


/**
 * HILFSFUNKTION: Gibt die endgültige URL für die Bildvorschau zurück.
 */
const getFinalPreviewUrl = (formData, previewImage) => {
    // 1. Wenn ein neues File ausgewählt wurde (lokale Vorschau)
    if (previewImage && typeof previewImage === 'string' && previewImage.startsWith('blob:')) {
        return previewImage;
    }
    // 2. Wenn eine vorhandene imageUrl (von Backend) existiert
    if (formData.imageUrl && formData.imageUrl.startsWith('/uploads/')) {
        return `${BACKEND_URL}${formData.imageUrl}`;
    }
    // 3. Fallback für externe URLs oder leere Bilder
    return formData.imageUrl || FALLBACK_IMAGE_URL;
};

// Wird verwendet, um den Zustand bei Bearbeitung oder neuer Erstellung zu initialisieren
const getInitialState = (productToEdit) => productToEdit || {
    name: '',
    description: '',
    price: 0,
    stock: 0, 
    // NEU: Verfügbare Abmessungen
    availableHeight: 0, 
    availableWidth: 0,
    // NEU: Verknüpfung zur Stoff-ID
    fabricId: '',
    isFeatured: false,
    imageFile: null,
    imageUrl: '', 
};

// --- Hauptkomponente: ProductForm ---
const ProductForm = ({ productToEdit, onSubmit, onCancel, fabrics = [] }) => {
    
    // useMemo stellt sicher, dass initialState nur neu berechnet wird, wenn sich productToEdit ändert
    const initialState = useMemo(() => getInitialState(productToEdit), [productToEdit]);
    
    const [formData, setFormData] = useState(initialState);
    const [isEditing, setIsEditing] = useState(!!productToEdit);
    const [previewImage, setPreviewImage] = useState(formData.imageUrl || null);


    useEffect(() => {
        // Setzt das Formular zurück, wenn ein neues Produkt geladen wird
        setFormData(initialState);
        setIsEditing(!!productToEdit);
        setPreviewImage(initialState.imageUrl || null);
    }, [initialState, productToEdit]); 
    

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        
        if (name === 'imageFile') {
            const file = e.target.files[0];
            setFormData(prev => ({ ...prev, imageFile: file }));
            // Lokale URL für die Vorschau erstellen
            if (file) {
                if (previewImage) URL.revokeObjectURL(previewImage); // Alte URL freigeben
                setPreviewImage(URL.createObjectURL(file));
            } else {
                setPreviewImage(formData.imageUrl || null);
            }
        } else if (type === 'checkbox') {
            setFormData(prev => ({ ...prev, [name]: checked }));
        } else if (name === 'price' || name === 'stock' || name === 'availableHeight' || name === 'availableWidth') {
            // Konvertiert den Wert in eine Zahl und stellt sicher, dass er nicht negativ ist
            const numericValue = parseFloat(value);
            setFormData(prev => ({ 
                ...prev, 
                [name]: isNaN(numericValue) ? 0 : Math.max(0, numericValue) 
            }));
        } else {
            setFormData(prev => ({ ...prev, [name]: value }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        // Gibt die Daten an die onSubmit-Funktion im AdminDashboard weiter
        onSubmit(formData, isEditing);
    };

    // Style-Klassen sind in index.css definiert
    return (
        <form onSubmit={handleSubmit} className="admin-form-card">
            <h2 className="admin-form-title">
                {isEditing ? 'Produkt bearbeiten' : 'Neues Produkt erstellen'}
            </h2>

            {/* 1. Allgemeine Produktinformationen */}
            <div className="admin-form-group">
                <label htmlFor="name">Name:</label>
                <input
                    id="name"
                    name="name"
                    type="text"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />
            </div>
            
            <div className="admin-form-group">
                <label htmlFor="description">Beschreibung:</label>
                <textarea
                    id="description"
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    required
                    rows="4"
                />
            </div>

            {/* 2. Preis, Bestand und Abmessungen */}
            <div className="admin-form-row">
                <div className="admin-form-group">
                    <label htmlFor="price">Preis pro Stück/Einheit (€):</label>
                    <input
                        id="price"
                        name="price"
                        type="number"
                        step="0.01"
                        min="0"
                        value={formData.price}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="admin-form-group">
                    <label htmlFor="stock">Bestand (Einheiten):</label>
                    <input
                        id="stock"
                        name="stock"
                        type="number"
                        min="0"
                        value={formData.stock}
                        onChange={handleChange}
                        required
                    />
                </div>
            </div>

            <div className="admin-form-row">
                <div className="admin-form-group">
                    <label htmlFor="availableWidth">Verfügbare Breite (cm):</label>
                    <input
                        id="availableWidth"
                        name="availableWidth"
                        type="number"
                        min="0"
                        value={formData.availableWidth}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="admin-form-group">
                    <label htmlFor="availableHeight">Verfügbare Höhe/Länge (cm/m):</label>
                    <input
                        id="availableHeight"
                        name="availableHeight"
                        type="number"
                        min="0"
                        value={formData.availableHeight}
                        onChange={handleChange}
                        required
                    />
                </div>
            </div>
            
            {/* NEU: Stoffauswahl */}
            <div className="admin-form-group">
                <label htmlFor="fabricId">Zugeordneter Stoff:</label>
                <select
                    id="fabricId"
                    name="fabricId"
                    value={formData.fabricId}
                    onChange={handleChange}
                    // Nur optional, wenn Sie Zubehör ohne Stoffe verkaufen
                    // required 
                    className="admin-select"
                >
                    <option value="">-- Kein Stoff zugeordnet --</option>
                    {fabrics.map(fabric => (
                        <option key={fabric.id} value={fabric.id}>
                            {fabric.name} ({fabric.fabricType})
                        </option>
                    ))}
                </select>
                {fabrics.length === 0 && (
                    <p style={{ color: 'var(--farbe-grau-dunkel)', fontSize: '0.8em', marginTop: '5px' }}>
                        Bitte zuerst Stoffe in der Stoffverwaltung erstellen.
                    </p>
                )}
            </div>

            {/* 3. Bild-Upload */}
            <div className="admin-form-group" style={{ display: 'flex', gap: '20px' }}>
                <div style={{ flexGrow: 1 }}>
                    <label htmlFor="imageFile">Produktbild (optional):</label>
                    <input
                        type="file"
                        id="imageFile"
                        name="imageFile"
                        accept="image/*"
                        onChange={handleChange}
                    />
                    {isEditing && formData.imageUrl && (
                        <p style={{ fontSize: '0.8em', color: 'var(--farbe-grau-dunkel)', marginTop: '5px' }}>
                            {formData.imageUrl.includes('placehold') ? 'Kein Bild gespeichert' : `Aktuelles Bild: ${formData.imageUrl}`}
                        </p>
                    )}
                </div>
                
                <div style={{ width: '100px', flexShrink: 0 }}>
                    <img
                        src={getFinalPreviewUrl(formData, previewImage)}
                        alt="Vorschau"
                        style={{ maxWidth: '100%', maxHeight: '100px', borderRadius: '8px', border: '1px solid #ccc', objectFit: 'cover' }}
                        onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }}
                    />
                </div>
            </div>
            
            {/* 4. Featured-Checkbox */}
            <div className="admin-form-group" style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <input
                    type="checkbox"
                    id="isFeatured"
                    name="isFeatured"
                    checked={formData.isFeatured}
                    onChange={handleChange}
                    style={{ width: '20px', height: '20px' }}
                />
                <label htmlFor="isFeatured" style={{ margin: 0, fontWeight: 'normal', cursor: 'pointer' }}>
                    Als "Featured Product" auf der Startseite anzeigen
                </label>
            </div>
            
            {/* 5. Aktionen */}
            <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                <button type="submit" className="admin-form-submit-button">
                    {isEditing ? 'Änderungen speichern' : 'Produkt erstellen'}
                </button>
            </div>
            
            {isEditing && (
                <div style={{ marginTop: '15px', paddingTop: '15px', borderTop: '1px solid #eee' }}>
                    <button type="button" onClick={onCancel} className="admin-form-submit-button" style={{backgroundColor: '#666'}}>
                        Abbrechen (Zurück zur Erstellung)
                    </button>
                </div>
            )}
        </form>
    );
};

export default ProductForm;
