// client/src/components/AdminDashboard.js
import React, { useState, useEffect, useCallback } from 'react';
import { LogOut, Edit, Trash2 } from 'lucide-react';

const API_URL = 'http://localhost:3001/api';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/100x100/CCCCCC/333333?text=N/A";

// Auth Header
const getAuthHeaders = () => {
  const adminToken = localStorage.getItem('adminToken');
  return adminToken ? { Authorization: `Bearer ${adminToken}` } : {};
};

// Bildpfad helper
const getImageUrl = (imagePath) => {
  if (imagePath && imagePath.startsWith('/uploads/')) return `${BACKEND_URL}${imagePath}`;
  return imagePath || FALLBACK_IMAGE_URL;
};

// --- Produktformular ---
const ProductForm = ({ productToEdit, onSubmit, onCancel, fabrics }) => {
  const [formData, setFormData] = useState(productToEdit || {
    name: '', description: '', price: 0, stock: 0,
    isFeatured: false, fabricId: '', availableHeight: 0,
    availableWidth: 0, imageFile: null, imageUrl: ''
  });

  useEffect(() => setFormData(productToEdit || {
    name: '', description: '', price: 0, stock: 0,
    isFeatured: false, fabricId: '', availableHeight: 0,
    availableWidth: 0, imageFile: null, imageUrl: ''
  }), [productToEdit]);

  const handleChange = e => {
    const { name, value, type, checked, files } = e.target;
    if (name === 'imageFile') setFormData({ ...formData, imageFile: files[0] });
    else if (type === 'checkbox') setFormData({ ...formData, [name]: checked });
    else setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = e => { e.preventDefault(); onSubmit(formData); };

  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h3>{productToEdit ? "Produkt bearbeiten" : "Neues Produkt erstellen"}</h3>
      <div className="form-group">
        <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Produktname" required />
      </div>
      <div className="form-group">
        <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Beschreibung" required />
      </div>
      <div className="form-group">
        <p>Preis</p>
        <input type="number" name="price" value={formData.price} onChange={handleChange} placeholder="Preis (€)" step="0.01" min="0" required />
      </div>
      <div className="form-group">
        <p>Bestand</p>
        <input type="number" name="stock" value={formData.stock} onChange={handleChange} placeholder="Bestand" min="0" required />
      </div>
      <div className="form-group">
        <select name="fabricId" value={formData.fabricId} onChange={handleChange}>
          <option value="">-- Stoff wählen --</option>
          {fabrics.map(f => <option key={f.id} value={f.id}>{f.name}</option>)}
        </select>
      </div>
      <label>
        <input type="checkbox" name="isFeatured" checked={formData.isFeatured} onChange={handleChange} />
        Als "Angebot" anzeigen
      </label>
      <div className="form-group">
        <input type="file" name="imageFile" onChange={handleChange} accept="image/*" />
      </div>
      <button type="submit">{productToEdit ? "Speichern" : "Erstellen"}</button>
      {productToEdit && <button type="button" onClick={onCancel} style={{marginLeft:'10px'}}>Abbrechen</button>}
    </form>
  );
};

// --- Stoffformular ---
const FabricForm = ({ onAdd }) => {
  const [formData, setFormData] = useState({ name: '', fabricType: '', stock: 0, imageFile: null });

  const handleChange = e => {
    const { name, value, files } = e.target;
    if (name === 'imageFile') setFormData({ ...formData, imageFile: files[0] });
    else setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', formData.name);
    data.append('fabricType', formData.fabricType);
    data.append('stock', formData.stock);
    if (formData.imageFile) data.append('imageFile', formData.imageFile);
    onAdd(data);
  };

  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h3>Neuen Stoff hinzufügen</h3>
      <input name="name" value={formData.name} onChange={handleChange} placeholder="Name" required />
      <input name="fabricType" value={formData.fabricType} onChange={handleChange} placeholder="Typ" required />
      <input name="stock" type="number" value={formData.stock} onChange={handleChange} min="0" required />
      <input type="file" name="imageFile" onChange={handleChange} accept="image/*" />
      <button type="submit">Speichern</button>
    </form>
  );
};

// --- Hauptkomponente ---
const AdminDashboard = ({ onLogout }) => {
  const [products, setProducts] = useState([]);
  const [fabrics, setFabrics] = useState([]);
  const [currentProduct, setCurrentProduct] = useState(null);
  const [message, setMessage] = useState("");

  const fetchAll = useCallback(async () => {
    try {
      const prodRes = await fetch(`${API_URL}/products`, { headers: getAuthHeaders() });
      const fabRes = await fetch(`${API_URL}/fabrics`, { headers: getAuthHeaders() });

      if (!prodRes.ok || !fabRes.ok) throw new Error("Fehler beim Laden der Daten");

      setProducts(await prodRes.json());
      setFabrics(await fabRes.json());
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  }, []);

  useEffect(() => { fetchAll(); }, [fetchAll]);

  // Produkt speichern
  const handleProductSubmit = async (data) => {
    try {
      const formData = new FormData();
      Object.keys(data).forEach(key => { if (data[key] !== null) formData.append(key, data[key]); });
      const method = data.id ? "PUT" : "POST";
      const url = data.id ? `${API_URL}/products/${data.id}` : `${API_URL}/products`;

      const res = await fetch(url, { method, headers: getAuthHeaders(), body: formData });
      if (!res.ok) throw new Error("Fehler beim Speichern");

      setMessage("✅ Produkt gespeichert");
      setCurrentProduct(null);
      fetchAll();
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  };

  // Produkt löschen
  const handleDeleteProduct = async id => {
    if (!window.confirm("Produkt wirklich löschen?")) return;
    try {
      const res = await fetch(`${API_URL}/products/${id}`, { method: "DELETE", headers: getAuthHeaders() });
      if (!res.ok) throw new Error("Fehler beim Löschen");
      setMessage("✅ Produkt gelöscht");
      fetchAll();
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  };

  // Stoff hinzufügen
  const handleAddFabric = async (formData) => {
    try {
      const res = await fetch(`${API_URL}/fabrics`, { method: "POST", headers: getAuthHeaders(), body: formData });
      if (!res.ok) throw new Error("Fehler beim Speichern");
      setMessage("✅ Stoff gespeichert");
      fetchAll();
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  };

  // Stoff löschen
  const handleDeleteFabric = async id => {
    if (!window.confirm("Stoff wirklich löschen?")) return;
    try {
      const res = await fetch(`${API_URL}/fabrics/${id}`, { method: "DELETE", headers: getAuthHeaders() });
      if (!res.ok) throw new Error("Fehler beim Löschen");
      setMessage("✅ Stoff gelöscht");
      fetchAll();
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  };

  return (
    <div className="admin-dashboard-container">
      <h1>Admin Dashboard</h1>
      <button onClick={() => { localStorage.removeItem('adminToken'); onLogout(); }}><LogOut /> Logout</button>
      {message && <p className={`status-message ${message.includes("❌") ? "status-message-error" : "status-message-success"}`}>{message}</p>}

      <div className="admin-dashboard-grid">
        <ProductForm productToEdit={currentProduct} onSubmit={handleProductSubmit} onCancel={() => setCurrentProduct(null)} fabrics={fabrics} />
        <div className="admin-list-container">
          <h3>Produkte ({products.length})</h3>
          {products.map(p => (
            <div key={p.id} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
              <img src={getImageUrl(p.imageUrl)} alt="" style={{ width: '50px', height: '50px', objectFit:'cover' }} />
              <span style={{ flex: 1, marginLeft: '10px' }}>{p.name}</span>
              <button className="admin-button admin-button-edit" onClick={() => setCurrentProduct(p)}><Edit /></button>
              <button className="admin-button admin-button-delete" onClick={() => handleDeleteProduct(p.id)}><Trash2 /></button>
            </div>
          ))}
        </div>
      </div>

      <div className="admin-dashboard-grid" style={{marginTop:'40px'}}>
        <FabricForm onAdd={handleAddFabric} />
        <div className="admin-list-container">
          <h3>Stoffe ({fabrics.length})</h3>
          {fabrics.map(f => (
            <div key={f.id} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
              <img src={getImageUrl(f.imageUrl)} alt="" style={{ width: '50px', height: '50px', objectFit:'cover' }} />
              <span style={{ flex: 1, marginLeft: '10px' }}>{f.name}</span>
              <button className="admin-button admin-button-delete" onClick={() => handleDeleteFabric(f.id)}><Trash2 /></button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
