import { Routes, Route, Link } from 'react-router-dom';
// Importiert die jetzt erstellten Komponenten
import ImageCarousel from './components/ImageCarousel';
import AdminDashboard from './components/AdminDashboard'; 

// FIX: Platzhalter-URL f√ºr das Logo, um den Asset-Fehler zu vermeiden

import './index.css'; 

// --- Platzhalter-Komponenten f√ºr die Seiten ---

const HomePage = () => (
  <div style={{ padding: '20px', textAlign: 'center' }}>
    <img 
      src={LOGO_URL} // Verwendung der Platzhalter-URL
      alt="Henkes-Stoffzauber Logo" 
      style={{ width: '500px', margin: '20px auto', display: 'block' }}
    />
    <h2>Willkommen im N√§h-Paradies!</h2>
    <p>Hier finden Sie die neuesten Schnitte, Stoffe und Zubeh√∂r f√ºr Ihre M√ºtzen, Schals und Taschen. Starten Sie jetzt Ihre Kreativit√§t!</p>

    <ImageCarousel />

