    {/* <Link to="/shop" className="nav-button">Zum Shop</Link>
    <Link to="/blog" className="nav-button" style={{ marginLeft: '10px' }}>Zum Blog</Link> */}
  </div>
);

const ShopPage = () => (
  <div style={{ padding: '20px' }}>
    <h2>üõçÔ∏è Der Stoffzauber-Shop</h2>
    <p>Hier kommen bald die Produkte aus der Node.js/MongoDB-Datenbank.</p>
  </div>
);

const BlogPage = () => (
  <div style={{ padding: '20px' }}>
    <h2>‚úÇÔ∏è Andreas N√§htipps & Blog</h2>
    <p>Neueste Anleitungen und Inspirationen f√ºr kreative N√§hprojekte.</p>
  </div>
);

// --- Haupt-App-Layout ---
function App() {
  return (
    <div className="App">

      <nav>
        <Link to="/">Home</Link>
        <Link to="/shop">Shop</Link>
        <Link to="/blog">Blog</Link>
        {/* Tempor√§rer Admin-Link zum Testen */}
        <Link to="/admin" style={{ marginLeft: 'auto', fontWeight: 'normal', color: 'var(--farbe-akzent)' }}>Admin</Link>
      </nav>

      <main>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/shop" element={<ShopPage />} />
          <Route path="/blog" element={<BlogPage />} />
          {/* Route f√ºr das Admin Dashboard (jetzt mit der echten Komponente) */}
          <Route path="/admin" element={<AdminDashboard />} />
          <Route path="*" element={<h2 style={{padding: '20px'}}>404 - Seite nicht gefunden</h2>} />
        </Routes>
      </main>

      <footer className="App-footer">
        ¬© 2025 Henkes-Stoffzauber.de
      </footer>
    </div>
  );
}

export default App;
