import React, { useState, useEffect } from 'react';
import { useCart } from '../contexts/CartContext'; // Import des Warenkorb-Hooks

const API_URL = 'http://localhost:3001/api/products';

// Hilfsfunktion zur Generierung der korrekten Bild-URL
const getImageUrl = (imagePath) => {
    // Wenn der Pfad mit '/uploads/' beginnt, muss die Basis-URL hinzugef√ºgt werden
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `http://localhost:3001${imagePath}`;
    }
    // Ansonsten ist es eine externe URL oder ein Fallback-Pfad
    return imagePath;
};


const ProductDetail = ({ productId, navigate }) => { // navigate hinzugef√ºgt
    const { addToCart } = useCart(); // Warenkorb-Funktion
    
    const [product, setProduct] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [quantity, setQuantity] = useState(1); // Lokaler Zustand f√ºr die Menge
    const [message, setMessage] = useState(''); // Lokale Nachricht (z.B. "Zum Warenkorb hinzugef√ºgt")

    useEffect(() => {
        if (!productId) {
            setError('Keine Produkt-ID in der URL angegeben.');
            setIsLoading(false);
            return;
        }
        
        // Timeout f√ºr die Nachricht
        let messageTimer;
        if (message) {
            messageTimer = setTimeout(() => setMessage(''), 3000);
        }

        const fetchProduct = async () => {
            try {
                const response = await fetch(`${API_URL}/${productId}`);
                
                if (response.status === 404) {
                    throw new Error(`Produkt mit ID ${productId} nicht gefunden.`);
                }
                
                if (!response.ok) {
                    throw new Error(`Fehler beim Laden des Produkts: Serverfehler ${response.status}.`);
                }
                
                const data = await response.json();
                setProduct(data);
                
            } catch (e) {
                console.error("Fehler beim Abrufen des Produkts:", e);
                setError(e.message);
                setProduct(null);
            } finally {
                setIsLoading(false);
            }
        };

        fetchProduct();
        
        return () => clearTimeout(messageTimer);
    }, [productId, message]); // message als Abh√§ngigkeit, um den Timer neu zu starten

    // --- Warenkorb-Handler ---
    const handleAddToCart = () => {
        if (product && quantity > 0 && product.stock > 0) {
            addToCart(product, quantity);
            setMessage(`${quantity}x ${product.name} zum Warenkorb hinzugef√ºgt!`);
            setQuantity(1); // Menge zur√ºcksetzen
        } else if (product.stock === 0) {
            setMessage('Dieses Produkt ist leider ausverkauft.');
        } else {
             setMessage('Bitte geben Sie eine g√ºltige Menge ein.');
        }
    };


    // --- Styles ---
    const styles = {
        container: {
            maxWidth: '1200px',
            margin: '50px auto',
            padding: '40px',
            backgroundColor: 'var(--farbe-hell, #ffffff)',
            borderRadius: '15px',
            boxShadow: '0 10px 30px rgba(0, 0, 0, 0.1)',
        },
        productWrapper: {
            display: 'flex',
            gap: '40px',
            flexWrap: 'wrap', // Responsive
        },
        imageSection: {
            flex: '1 1 400px', // Flexible Breite, Basis 400px
            maxWidth: '100%',
        },
        image: {
            width: '100%',
            height: 'auto',
            maxHeight: '500px',
            objectFit: 'cover',
            borderRadius: '12px',
            boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
        },
        infoSection: {
            flex: '1 1 500px', // Flexible Breite, Basis 500px
            padding: '10px 0',
        },
        title: {
            fontSize: '2.8em',
            margin: '0 0 10px 0',
            color: 'var(--farbe-prim√§r)',
        },
        price: {
            fontSize: '2em',
            fontWeight: '700',
            margin: '10px 0 20px 0',
            color: 'var(--farbe-akzent)',
        },
        stockIn: {
            fontSize: '1em',
            fontWeight: '600',
            color: 'var(--farbe-gruen)',
        },
        stockOut: {
            fontSize: '1em',
            fontWeight: '600',
            color: 'var(--farbe-prim√§r)',
        },
        divider: {
            border: '0',
            borderTop: '1px solid #eee',
            margin: '20px 0',
        },
        subtitle: {
            fontSize: '1.4em',
            margin: '0 0 10px 0',
            color: 'var(--farbe-text-dunkel)',
        },
        descriptionBox: {
            padding: '15px',
            backgroundColor: 'var(--farbe-akzent-hell)',
            borderRadius: '8px',
            lineHeight: '1.6',
            color: 'var(--farbe-text-dunkel)',
            marginBottom: '30px',
        },
        actionSection: {
            marginTop: '20px',
            display: 'flex',
            gap: '15px',
            alignItems: 'center',
            flexWrap: 'wrap',
        },
        quantityInput: {
            padding: '12px',
            fontSize: '1.1em',
            borderRadius: '8px',
            border: '1px solid #ccc',
            width: '80px',
            textAlign: 'center',
        },
        addToCartButton: {
            padding: '15px 30px',
            fontSize: '1.1em',
            borderRadius: '8px',
            border: 'none',
            backgroundColor: 'var(--farbe-akzent)',
            color: 'white',
            fontWeight: '700',
            cursor: 'pointer',
            transition: 'background-color 0.2s',
        },
        messageBox: {
            padding: '10px',
            backgroundColor: 'var(--farbe-gruen-hell)',
            color: 'var(--farbe-gruen)',
            borderRadius: '8px',
            textAlign: 'center',
            marginTop: '20px',
            fontWeight: '600',
        }
    };


    // --- Render-Logik ---

    if (isLoading) {
        return <p style={{ textAlign: 'center', padding: '50px' }}>üßµ Produktdetails werden geladen...</p>;
    }

    if (error) {
        return <p style={{ textAlign: 'center', color: 'red', padding: '50px' }}>Fehler: {error}</p>;
    }

    // Wenn product null ist (nach Fehler oder Ladezeit), soll nichts gerendert werden
    if (!product) return null;


    const productImageUrl = getImageUrl(product.imageUrl);
    const isInStock = product.stock > 0;

    return (
        <div style={styles.container}>
            <button 
                onClick={() => navigate('/shop')} 
                style={{ background: 'none', border: 'none', color: 'var(--farbe-prim√§r)', cursor: 'pointer', marginBottom: '20px', fontSize: '1.1em' }}
            >
                &larr; Zur√ºck zum Shop
            </button>
            
            <div style={styles.productWrapper}>
                
                {/* 1. Bildbereich */}
                <div style={styles.imageSection}>
                    <img 
                        src={productImageUrl} 
                        alt={product.name} 
                        style={styles.image}
                        onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/400x500/ccc/333?text=Bild+Fehlt'; }}
                    />
                </div>

                {/* 2. Informationsbereich */}
                <div style={styles.infoSection}>
                    <h1 style={styles.title}>{product.name}</h1>
                    
                    {/* Preis und Verf√ºgbarkeit */}
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'baseline', marginBottom: '20px' }}>
                        <p style={styles.price}>‚Ç¨ {product.price.toFixed(2)}</p>
                        <p style={isInStock ? styles.stockIn : styles.stockOut}>
                            {isInStock ? `Lagernd (${product.stock} verf√ºgbar)` : 'Ausverkauft'}
                        </p>
                    </div>

                    <hr style={styles.divider} />

                    {/* Kurzbeschreibung */}
                    <h2 style={styles.subtitle}>Produktbeschreibung</h2>
                    <div style={styles.descriptionBox}>
                        <p style={{ margin: 0 }}>
                            {product.description || 'Keine Beschreibung vorhanden.'}
                        </p>
                    </div>

                    {/* Aktionen: Menge und Button */}
                    <h2 style={styles.subtitle}>In den Warenkorb</h2>
                    <div style={styles.actionSection}>
                        
                        {/* Mengen-Input */}
                        <input
                            type="number"
                            min="1"
                            max={product.stock}
                            value={quantity}
                            onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
                            style={styles.quantityInput}
                            disabled={!isInStock}
                        />

                        {/* Warenkorb-Button */}
                        <button 
                            style={styles.addToCartButton}
                            onClick={handleAddToCart}
                            disabled={!isInStock}
                            onMouseEnter={(e) => e.target.style.backgroundColor = isInStock ? 'var(--farbe-sekund√§r)' : styles.addToCartButton.backgroundColor} 
                            onMouseLeave={(e) => e.target.style.backgroundColor = isInStock ? 'var(--farbe-akzent)' : styles.addToCartButton.backgroundColor}
                        >
                            {isInStock ? 'In den Warenkorb' : 'Nicht verf√ºgbar'}
                        </button>
                    </div>
                    
                    {/* Lokale Best√§tigungsnachricht */}
                    {message && (
                        <div style={styles.messageBox}>
                            {message}
                        </div>
                    )}

                </div>
            </div>
        </div>
    );
};

export default ProductDetail;
