import React, { useEffect, useState } from 'react';
import { useCart } from './CartContext';
import './Checkout.css';

export default function Checkout({ address }) {
  const { cartItems, clearCart } = useCart();
  const [orderId, setOrderId] = useState(null);
  const [invoicePath, setInvoicePath] = useState(null);

  useEffect(() => {
    if (window.paypal && cartItems.length) {
      window.paypal.Buttons({
        createOrder: async (_, actions) => {
          const res = await fetch('http://localhost:3001/api/checkout', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ cart: cartItems, address, paymentMethod: 'paypal' })
          });
          const data = await res.json();
          if (!data.paypalOrderId) throw new Error("PayPal Order konnte nicht erstellt werden");
          setOrderId(data.orderId);
          setInvoicePath(data.invoicePath);
          return data.paypalOrderId;
        },
        onApprove: async (_, actions) => {
          const capture = await actions.order.capture();
          console.log("Zahlung abgeschlossen:", capture);
          clearCart();
        },
        onError: err => console.error(err)
      }).render('#paypal-button-container');
    }
  }, [cartItems, address, clearCart]);

  return (
    <div className="checkout-container">
      <h2>Checkout</h2>
      <div id="paypal-button-container"></div>
      {invoicePath && (
        <a href={`http://localhost:3001${invoicePath}`} target="_blank" rel="noopener noreferrer" className="checkout-download">
          Rechnung herunterladen
        </a>
      )}
    </div>
  );
}
