import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';
import logoAsset from '../assets/logo.jpg';
import ImageCarousel from './ImageCarousel';
import FabricCarousel from './FabricCarousel'; // <-- Import hinzuf√ºgen

const API_URL = 'http://localhost:3001/api';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = 'https://placehold.co/200x200/CCCCCC/333333?text=N/A';

const getImageUrl = (imagePath) => {
  if (imagePath && imagePath.startsWith('/uploads/')) return `${BACKEND_URL}${imagePath}`;
  return imagePath || FALLBACK_IMAGE_URL;
};

const Home = () => {
  const navigate = useNavigate(); 
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [featuredFabrics, setFeaturedFabrics] = useState([]);

  useEffect(() => {
    // Featured-Produkte laden
    fetch(`${API_URL}/products/featured`)
      .then(res => res.json())
      .then(data => setFeaturedProducts(data))
      .catch(err => console.error('Fehler beim Laden der Produkte:', err));

    // Featured-Stoffe laden
    const token = localStorage.getItem('adminToken');
    fetch(`${API_URL}/fabrics/featured`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setFeaturedFabrics(data))
      .catch(err => console.error('Fehler beim Laden der Stoffe:', err));
  }, []);

  const handleShopClick = (e) => {
    e.preventDefault();
    navigate('/shop'); 
  };

  return (
    <div className="home-container">
      {/* 1. Intro */}
      <div className="home-intro">
        <img 
          src={logoAsset} 
          alt="Henkes Stoffzauber Logo" 
          className="home-logo"
          onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/350x150/6f42c1/ffffff?text=LOGO+FEHLT'; }}
        />
        <h1 className="home-title">Willkommen bei Henkes Stoffzauber</h1>
        <p className="home-text">
          Entdecken Sie unsere handverlesene Auswahl an hochwertigen Stoffen und kreativen N√§h-Ideen. 
        </p>
        <button className="home-button" onClick={handleShopClick}>Zum Shop</button>
      </div>

      {/* 2. Featured Produkte */}
      <h2 className="home-subtitle">‚≠ê Unsere Highlights der Woche ‚≠ê</h2>
      <ImageCarousel items={featuredProducts} getImageUrl={getImageUrl} />

      {/* 3. Featured Stoffe */}
      <h2 className="home-subtitle">üßµ Neue Stoff-Highlights üßµ</h2>
      <FabricCarousel items={featuredFabrics} getImageUrl={getImageUrl} />

      {/* 4. Info */}
      <div className="home-info">
        <h2 className="home-info-title">Qualit√§t, die man f√ºhlt</h2>
        <p className="home-text">
          Jeder Stoff in unserem Sortiment wird sorgf√§ltig ausgew√§hlt, um h√∂chste Anspr√ºche an Haptik,
          Haltbarkeit und Farbtreue zu erf√ºllen.
        </p>
      </div>
    </div>
  );
};

export default Home;
