import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from './CartContext';
import '../index.css';

const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/CCCCCC/333333?text=Stoffmuster";

const getAuthHeaders = () => {
  const token = localStorage.getItem('adminToken');
  return token ? { Authorization: `Bearer ${token}` } : {};
};

const getImageUrl = (imagePath) => {
  if (imagePath && imagePath.startsWith('/uploads/')) return `${BACKEND_URL}${imagePath}`;
  return imagePath || FALLBACK_IMAGE_URL;
};

// ---------------- Product Card ----------------
const ProductCard = ({ product, onAdd }) => {
  const isInStock = product.stock > 0;

  return (
    <div className="product-card">
      <Link to={`/shop/${product.id}`}>
        <img
          src={getImageUrl(product.imageUrl)}
          alt={product.name}
          onError={e => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }}
        />
      </Link>
      <h3>{product.name}</h3>
      <p style={{ fontWeight: 'bold', color: 'var(--farbe-akzent)' }}>‚Ç¨ {product.price.toFixed(2)}</p>
      <p style={{ color: isInStock ? 'green' : 'red', fontWeight: 'bold' }}>
        {isInStock ? 'Sofort verf√ºgbar' : 'Ausverkauft'}
      </p>
      <button
        onClick={() => onAdd(product)}
        disabled={!isInStock}
      >
        {isInStock ? 'In den Warenkorb' : 'Nicht lieferbar'}
      </button>
    </div>
  );
};

// ---------------- Shop ----------------
const Shop = () => {
  const { cartItems, addToCart } = useCart();
  const [products, setProducts] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await fetch(API_URL, { headers: getAuthHeaders() });
        if (!res.ok) throw new Error(`Netzwerkfehler: ${res.status}`);
        const data = await res.json();
        setProducts(data);
        setFiltered(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  useEffect(() => {
    const lower = search.toLowerCase();
    setFiltered(products.filter(p => p.name.toLowerCase().includes(lower)));
  }, [search, products]);

  const handleAddToCart = (product) => {
    addToCart(product, 1);
    setShowCart(true);
  };

  if (loading) return <p style={{textAlign:'center', padding:'50px'}}>Shop wird geladen...</p>;
  if (error) return <p style={{textAlign:'center', color:'red', padding:'50px', fontWeight:'bold'}}>Fehler: {error}</p>;

  return (
    <div className="shop-container">
      <h1 style={{ textAlign:'center', color:'var(--farbe-prim√§r)', marginBottom:'40px' }}>Schau dich um</h1>

      <input
        type="text"
        placeholder="Suche ..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        className="search-input"
      />

      {filtered.length === 0 ? (
        <p style={{ textAlign:'center', color:'#666', padding:'50px' }}>Kein Produkt gefunden.</p>
      ) : (
        <div className="product-grid">
          {filtered.map(p => (
            <ProductCard key={p.id} product={p} onAdd={handleAddToCart} />
          ))}
        </div>
      )}

      {/* Sticky Warenkorb */}
      <div className={`sticky-cart ${showCart ? 'open' : ''}`}>
        <h3>üõí Warenkorb ({cartItems.reduce((sum, i) => sum + i.quantity, 0)})</h3>
        {cartItems.length === 0 ? (
          <p>Dein Warenkorb ist leer.</p>
        ) : (
          <ul>
            {cartItems.map(item => (
              <li key={item.id}>
                {item.name} x {item.quantity} (‚Ç¨ {(item.price*item.quantity).toFixed(2)})
              </li>
            ))}
          </ul>
        )}
        <button onClick={() => setShowCart(false)}>Schlie√üen</button>
      </div>

      {/* Button zum √ñffnen Warenkorb */}
      <button className="open-cart-button" onClick={() => setShowCart(!showCart)}>
        üõí {cartItems.reduce((sum, i) => sum + i.quantity, 0)}
      </button>
    </div>
  );
};

export default Shop;
