import React, { createContext, useContext, useState } from 'react';

// 1. Context erstellen: Wird verwendet, um den State global verfügbar zu machen.
const CartContext = createContext();

// 2. Custom Hook erstellen: Vereinfacht den Zugriff auf den Context in anderen Komponenten.
// Jede Komponente kann einfach 'const { cart, addToCart } = useCart();' aufrufen.
export const useCart = () => {
    return useContext(CartContext);
};

// 3. Provider-Komponente erstellen: Verwaltet den State und stellt ihn bereit.
export const CartProvider = ({ children }) => {
    // Der Warenkorb-Zustand: Ein Array von Produkten { id, name, price, quantity }
    const [cart, setCart] = useState([]);

    // Fügt ein Produkt zum Warenkorb hinzu oder erhöht die Menge.
    const addToCart = (product, quantity = 1) => {
        setCart(prevCart => {
            const existingItem = prevCart.find(item => item.id === product.id);

            if (existingItem) {
                // Produkt existiert: Menge aktualisieren
                return prevCart.map(item =>
                    item.id === product.id
                        ? { ...item, quantity: item.quantity + quantity }
                        : item
                );
            } else {
                // Neues Produkt: Hinzufügen
                return [...prevCart, { ...product, quantity }];
            }
        });
    };

    // Entfernt ein Produkt komplett oder reduziert die Menge.
    const removeFromCart = (productId, removeAll = false) => {
        setCart(prevCart => {
            const existingItem = prevCart.find(item => item.id === productId);

            if (!existingItem) {
                return prevCart; // Nichts zu tun, wenn das Produkt nicht da ist
            }

            if (removeAll || existingItem.quantity <= 1) {
                // Produkt komplett entfernen
                return prevCart.filter(item => item.id !== productId);
            } else {
                // Menge um 1 reduzieren
                return prevCart.map(item =>
                    item.id === productId
                        ? { ...item, quantity: item.quantity - 1 }
                        : item
                );
            }
        });
    };

    // Leert den gesamten Warenkorb.
    const clearCart = () => {
        setCart([]);
    };

    // Der Wert, der über den Context an alle Konsumenten weitergegeben wird.
    const value = {
        cart,
        addToCart,
        removeFromCart,
        clearCart,
        // Zusätzlicher Wert: Die Gesamtzahl der Artikel im Warenkorb
        cartItemCount: cart.reduce((total, item) => total + item.quantity, 0),
        // Zusätzlicher Wert: Der Gesamtpreis
        cartTotal: cart.reduce((total, item) => total + (item.price * item.quantity), 0),
    };

    return (
        <CartContext.Provider value={value}>
            {children}
        </CartContext.Provider>
    );
};
