// client/src/components/StoffForm.js
import React, { useState, useEffect, useMemo } from 'react';

// Verwendung von useMemo, um ESLint-Warnung (exhaustive-deps) zu beheben
const getInitialState = (stoffToEdit) => stoffToEdit || {
    name: '',
    description: '',
    fabricType: '', // Neu: Stoffart (z.B. Jersey, French Terry)
    suitableFor: [], // Neu: Array von Verwendungszwecken
    stock: 0, 
    imageUrl: '', 
    imageFile: null,
};

// Liste der möglichen Verwendungszwecke (zum Checkbox-Rendern)
const USAGE_OPTIONS = [
    'Mütze (Außen)', 'Mütze (Innen)', 'Bündchen', 'Bekleidung (Oberteil)', 
    'Bekleidung (Hose)', 'Babybekleidung', 'Deko', 'Taschen', 'Accessoires'
];

// Liste der Stoffarten
const FABRIC_TYPES = [
    'Jersey', 'French Terry (Sommersweat)', 'Sweat', 'Bündchenstoff', 'Musselin', 
    'Webware Baumwolle', 'Cord', 'Fleece', 'Softshell', 'Leder (Imitat/Echt)'
];


const StoffForm = ({ stoffToEdit, onSubmit, onCancel }) => {
    
    const initialState = useMemo(() => getInitialState(stoffToEdit), [stoffToEdit]);
    
    const [formData, setFormData] = useState(initialState);
    const [isEditing, setIsEditing] = useState(!!stoffToEdit);
    const [previewImage, setPreviewImage] = useState(formData.imageUrl || null);


    useEffect(() => {
        // Setzt das Formular zurück, wenn ein neues oder kein Stoff zum Bearbeiten übergeben wird
        setFormData(initialState);
        setIsEditing(!!stoffToEdit);
        setPreviewImage(initialState.imageUrl || null);
    }, [initialState, stoffToEdit]); 
    

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        
        if (type === 'file') {
            const file = e.target.files[0];
            setFormData(prev => ({ ...prev, imageFile: file }));
            setPreviewImage(file ? URL.createObjectURL(file) : formData.imageUrl);
        } else if (name === 'suitableFor') {
            // Spezielle Behandlung für die Checkbox-Liste der Verwendungszwecke
            const usage = value;
            setFormData(prev => {
                const currentUsage = prev.suitableFor || [];
                if (checked) {
                    // Hinzufügen, falls noch nicht enthalten
                    return { ...prev, suitableFor: [...new Set([...currentUsage, usage])] };
                } else {
                    // Entfernen
                    return { ...prev, suitableFor: currentUsage.filter(item => item !== usage) };
                }
            });
        }
        else {
            setFormData(prev => ({
                ...prev,
                [name]: type === 'checkbox' ? checked : value,
            }));
        }
    };
    
    // Hilfsfunktion zum Prüfen, ob ein Verwendungszweck bereits ausgewählt ist
    const isUsageSelected = (usage) => (formData.suitableFor || []).includes(usage);


    const handleSubmit = async (e) => {
        e.preventDefault();
        
        const data = new FormData();
        data.append('name', formData.name);
        data.append('description', formData.description);
        data.append('stock', formData.stock);
        data.append('fabricType', formData.fabricType);
        // Da suitableFor ein Array ist, muss es als JSON-String übergeben werden
        data.append('suitableFor', JSON.stringify(formData.suitableFor)); 

        // Nur das Bild hinzufügen, wenn eine neue Datei ausgewählt wurde
        if (formData.imageFile) {
            data.append('image', formData.imageFile);
        }
        // Wenn es ein Bearbeitungsprozess ist, die ID mitsenden
        if (isEditing) {
            data.append('id', stoffToEdit.id);
        }
        
        // Senden des FormData-Objekts an die onSubmit-Funktion im Dashboard
        onSubmit(data, isEditing);
    };

    // Hilfsfunktion zur Generierung der korrekten Bild-URL für die Vorschau
    const getFinalPreviewUrl = () => {
        // Wenn ein lokales File ausgewählt wurde, ist previewImage eine Blob-URL
        if (previewImage && previewImage.startsWith('blob:')) {
            return previewImage;
        }
        // Ansonsten ist es die URL aus den Stoffdaten (von Firestore/Backend)
        if (previewImage && previewImage.startsWith('/uploads/')) {
             return `http://localhost:3001${previewImage}`;
        }
        return previewImage || 'https://placehold.co/100x100/ccc/333?text=Kein+Bild';
    };

    return (
        <form onSubmit={handleSubmit} className="admin-form-card">
            <h2 className="admin-form-title">
                {isEditing ? `Stoff bearbeiten: ${stoffToEdit.name}` : 'Neuen Stoff anlegen'}
            </h2>

            {/* 1. Allgemeine Infos */}
            <div className="admin-form-group">
                <label htmlFor="name">Name des Stoffs:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />
            </div>
            
            <div className="admin-form-group">
                <label htmlFor="description">Beschreibung:</label>
                <textarea
                    id="description"
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    rows="4"
                    required
                />
            </div>
            
            <div className="admin-form-group">
                <label htmlFor="stock">Bestand (in Metern):</label>
                <input
                    type="number"
                    id="stock"
                    name="stock"
                    min="0"
                    step="0.1"
                    value={formData.stock}
                    onChange={handleChange}
                    required
                />
            </div>

            {/* 2. Spezifische Stoff-Eigenschaften */}
            <div className="admin-form-group">
                <label htmlFor="fabricType">Stoffart:</label>
                <select
                    id="fabricType"
                    name="fabricType"
                    value={formData.fabricType}
                    onChange={handleChange}
                    required
                >
                    <option value="">-- Bitte wählen --</option>
                    {FABRIC_TYPES.map(type => (
                        <option key={type} value={type}>{type}</option>
                    ))}
                </select>
            </div>
            
            {/* Checkboxen für Verwendungszwecke */}
            <div className="admin-form-group">
                <label>Geeignet für (Verwendungszwecke):</label>
                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
                    {USAGE_OPTIONS.map(usage => (
                        <div key={usage} className="flex items-center">
                            <input
                                type="checkbox"
                                id={`usage-${usage}`}
                                name="suitableFor"
                                value={usage}
                                checked={isUsageSelected(usage)}
                                onChange={handleChange}
                                style={{ width: '20px', height: '20px' }}
                            />
                            <label htmlFor={`usage-${usage}`} style={{ margin: 0, fontWeight: 'normal', cursor: 'pointer', marginLeft: '8px' }}>
                                {usage}
                            </label>
                        </div>
                    ))}
                </div>
            </div>
            
            {/* 3. Bild-Upload */}
            <div className="admin-form-group">
                <label htmlFor="imageFile">Stoffmuster Bild (optional):</label>
                <input
                    type="file"
                    id="imageFile"
                    name="imageFile"
                    accept="image/*"
                    onChange={handleChange}
                />
                
                <div style={{ marginTop: '15px' }}>
                    <img
                        src={getFinalPreviewUrl()}
                        alt="Vorschau"
                        style={{ maxWidth: '100px', maxHeight: '100px', borderRadius: '8px', border: '1px solid #ccc' }}
                    />
                </div>
            </div>

            {/* 4. Aktionen */}
            <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                <button type="submit" className="admin-form-submit-button">
                    {isEditing ? 'Änderungen speichern' : 'Stoff erstellen'}
                </button>
            </div>
            
            {isEditing && (
                <div style={{ marginTop: '15px', paddingTop: '15px', borderTop: '1px solid #eee' }}>
                    <button type="button" onClick={onCancel} className="admin-form-submit-button" style={{backgroundColor: '#666'}}>
                        Abbrechen (Zurück zur Erstellung)
                    </button>
                </div>
            )}
        </form>
    );
};

export default StoffForm;
