import React, { useState, useEffect } from 'react';
import { useCart } from './CartContext'; // KORRIGIERTER PFAD: Nun direkt aus dem Root-Verzeichnis

const API_URL = 'http://localhost:3001/api'; // API-Basis-URL
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/500x500/F2B2B4/4F3C3C?text=Stoffmuster";

// Hilfsfunktion zur Generierung der korrekten Bild-URL
const getImageUrl = (imagePath) => {
    // Wenn der Pfad mit '/uploads/' beginnt, muss die Basis-URL hinzugefügt werden
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `${BACKEND_URL}${imagePath}`;
    }
    // Ansonsten ist es eine externe URL oder ein Fallback-Pfad
    return imagePath || FALLBACK_IMAGE_URL;
};

// Hilfsfunktion zum Abrufen des Stoffnamens
const fetchFabricName = async (fabricId) => {
    if (!fabricId) return null;
    try {
        const response = await fetch(`${API_URL}/fabrics/${fabricId}`);
        if (!response.ok) {
            // Wenn der Stoff nicht gefunden wird, ist das kein kritischer Fehler, 
            // es wird nur kein Name angezeigt.
            console.warn(`Stoff mit ID ${fabricId} nicht gefunden.`);
            return null;
        }
        const fabric = await response.json();
        return fabric.name;
    } catch (error) {
        console.error("Fehler beim Abrufen des Stoffnamens:", error);
        return null;
    }
};


const ProductDetail = ({ productId, navigate }) => { // navigate hinzugefügt
    const { addToCart } = useCart(); // Warenkorb-Funktion
    
    const [product, setProduct] = useState(null);
    const [fabricName, setFabricName] = useState(null); // Zustand für den Stoffnamen
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [quantity, setQuantity] = useState(1); // Lokaler Zustand für die Menge
    const [message, setMessage] = useState(''); // Lokale Nachricht (z.B. "Zum Warenkorb hinzugefügt")

    const isInStock = product && product.stock > 0;

    useEffect(() => {
        const fetchProduct = async () => {
            setIsLoading(true);
            setError(null);
            setProduct(null);
            setFabricName(null);
            
            if (!productId) {
                // Wenn keine ID vorhanden ist, zur Shop-Seite umleiten
                navigate('/shop', { replace: true });
                return;
            }

            try {
                const response = await fetch(`${API_URL}/products/${productId}`);
                
                if (!response.ok) {
                    throw new Error('Produkt nicht gefunden oder Serverfehler.');
                }
                
                const data = await response.json();
                
                setProduct(data);
                
                // NEU: Stoffnamen abrufen
                if (data.fabricId) {
                    const name = await fetchFabricName(data.fabricId);
                    setFabricName(name);
                }

                // Menge auf maximalen Bestand begrenzen
                if (data.stock < quantity) {
                    setQuantity(Math.max(1, data.stock));
                }

            } catch (err) {
                console.error("Fehler beim Laden des Produkts:", err);
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        fetchProduct();
    }, [productId, navigate, quantity]); // quantity als Dependency, um die maximale Menge beim Laden neu zu berechnen

    const handleAddToCart = () => {
        if (!product || !isInStock) return;

        addToCart(product, quantity);
        setMessage(`${quantity}x ${product.name} wurde zum Warenkorb hinzugefügt.`);
        
        // Nachricht nach 3 Sekunden ausblenden
        setTimeout(() => setMessage(''), 3000);
    };


    const styles = {
        container: {
            maxWidth: '1000px',
            margin: '50px auto',
            padding: '20px',
        },
        productWrapper: {
            display: 'flex',
            gap: '30px',
            backgroundColor: 'var(--farbe-hell)',
            borderRadius: '12px',
            boxShadow: '0 8px 25px rgba(0, 0, 0, 0.1)',
            padding: '30px',
            // Responsive Anpassungen (simuliert durch inline-styles)
            '@media (max-width: 768px)': {
                flexDirection: 'column',
            }
        },
        imageSection: {
            flex: '1 1 40%',
            minWidth: '300px',
        },
        image: {
            width: '100%',
            height: 'auto',
            borderRadius: '8px',
            objectFit: 'cover',
            aspectRatio: '1 / 1', // Quadratisches Bild
        },
        detailsSection: {
            flex: '1 1 60%',
        },
        name: {
            color: 'var(--farbe-primär)',
            fontSize: '2.5em',
            marginBottom: '10px',
        },
        price: {
            color: 'var(--farbe-akzent)',
            fontSize: '2em',
            fontWeight: 'bold',
            marginBottom: '20px',
        },
        description: {
            color: 'var(--farbe-text-dunkel)',
            fontSize: '1.1em',
            lineHeight: '1.6',
            marginBottom: '20px',
        },
        stockInfo: {
            fontSize: '0.9em',
            color: isInStock ? 'var(--farbe-grün)' : 'var(--farbe-rot)',
            fontWeight: 'bold',
            marginBottom: '20px',
        },
        actionBox: {
            display: 'flex',
            alignItems: 'center',
            gap: '15px',
            marginTop: '30px',
        },
        quantityInput: {
            width: '60px',
            padding: '10px',
            border: '1px solid #ccc',
            borderRadius: '5px',
            textAlign: 'center',
            fontSize: '1em',
        },
        addToCartButton: {
            backgroundColor: isInStock ? 'var(--farbe-akzent)' : 'var(--farbe-grau)',
            color: 'white',
            padding: '10px 20px',
            borderRadius: '5px',
            border: 'none',
            cursor: isInStock ? 'pointer' : 'not-allowed',
            fontWeight: 'bold',
            transition: 'background-color 0.2s',
        },
        messageBox: {
            marginTop: '20px',
            padding: '10px',
            backgroundColor: 'var(--farbe-akzent-hell)',
            color: 'var(--farbe-akzent)',
            borderRadius: '5px',
            fontWeight: 'bold',
        },
        infoTable: {
            width: '100%',
            borderCollapse: 'collapse',
            marginTop: '20px',
            fontSize: '0.95em',
        },
        tableRow: {
            borderBottom: '1px solid #eee',
        },
        tableHeader: {
            padding: '10px 0',
            textAlign: 'left',
            color: 'var(--farbe-primär)',
            fontWeight: 'bold',
            width: '30%',
        },
        tableData: {
            padding: '10px 0',
            textAlign: 'left',
            color: 'var(--farbe-text-dunkel)',
        }
    };


    if (isLoading) {
        return <p style={{ textAlign: 'center', padding: '50px' }}>Produktdetails werden geladen...</p>;
    }

    if (error) {
        return <div style={styles.container}><p style={{ textAlign: 'center', color: 'var(--farbe-rot)', fontWeight: 'bold' }}>Fehler: {error}</p></div>;
    }

    if (!product) {
        return <div style={styles.container}><p style={{ textAlign: 'center', color: 'var(--farbe-text-dunkel)' }}>Produkt nicht verfügbar.</p></div>;
    }

    return (
        <div style={styles.container}>
            <div style={styles.productWrapper}>
                
                {/* 1. Bildbereich */}
                <div style={styles.imageSection}>
                    <img 
                        src={getImageUrl(product.imageUrl)} 
                        alt={product.name} 
                        style={styles.image}
                        onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }}
                    />
                </div>

                {/* 2. Detailbereich */}
                <div style={styles.detailsSection}>
                    <h1 style={styles.name}>{product.name}</h1>
                    <p style={styles.description}>{product.description}</p>
                    
                    {/* Zusätzliche Informationen als Tabelle */}
                    <table style={styles.infoTable}>
                        <tbody>
                            <tr style={styles.tableRow}>
                                <td style={styles.tableHeader}>Stoffart:</td>
                                <td style={styles.tableData}>{fabricName || 'N/A'}</td> 
                            </tr>
                            <tr style={styles.tableRow}>
                                <td style={styles.tableHeader}>Bestand:</td>
                                <td style={styles.tableData}>
                                    {product.stock > 0 ? `${product.stock} Einheiten verfügbar` : 'Momentan ausverkauft'}
                                </td>
                            </tr>
                            <tr style={styles.tableRow}>
                                <td style={styles.tableHeader}>Preis:</td>
                                <td style={styles.tableData}>
                                    <span style={{ ...styles.price, fontSize: '1em', margin: 0 }}>
                                        {product.price.toFixed(2)} €
                                    </span>
                                </td>
                            </tr>
                        </tbody>
                    </table>

                    
                    <p style={{...styles.price, marginTop: '30px', marginBottom: '10px'}}>
                        {product.price.toFixed(2)} €
                    </p>
                    
                    <div style={styles.stockInfo}>
                        {isInStock ? 'Auf Lager' : 'Nicht verfügbar'}
                    </div>

                    {/* Warenkorb-Aktion */}
                    <div style={styles.actionBox}>
                        
                        {/* Mengen-Eingabe */}
                        <input
                            type="number"
                            min="1"
                            max={product.stock}
                            value={quantity}
                            onChange={(e) => setQuantity(Math.max(1, Math.min(product.stock, parseInt(e.target.value) || 1)))}
                            style={styles.quantityInput}
                            disabled={!isInStock}
                        />

                        {/* Warenkorb-Button */}
                        <button 
                            style={styles.addToCartButton}
                            onClick={handleAddToCart}
                            disabled={!isInStock}
                            onMouseEnter={(e) => e.target.style.backgroundColor = isInStock ? 'var(--farbe-sekundär)' : styles.addToCartButton.backgroundColor} 
                            onMouseLeave={(e) => e.target.style.backgroundColor = isInStock ? 'var(--farbe-akzent)' : styles.addToCartButton.backgroundColor}
                        >
                            {isInStock ? 'In den Warenkorb' : 'Nicht verfügbar'}
                        </button>
                    </div>
                    
                    {/* Lokale Bestätigungsnachricht */}
                    {message && (
                        <div style={styles.messageBox}>
                            {message}
                        </div>
                    )}

                </div>
            </div>
        </div>
    );
};

export default ProductDetail;
