import React from 'react';
import { Routes, Route, Link } from 'react-router-dom';
// Importiert die jetzt erstellten Komponenten
import ImageCarousel from './components/ImageCarousel';
import AdminDashboard from './components/AdminDashboard';
import Shop from './components/Shop'; 

import logo from './assets/logo.jpg'; 
import './index.css'; 

// --- Platzhalter-Komponenten für die Seiten ---

const HomePage = () => (
  <div style={{ padding: '20px', textAlign: 'center' }}>
    <img 
      src={logo} 
      alt="Henkes-Stoffzauber Logo" 
      style={{ width: '500px', margin: '20px auto', display: 'block' }}
    />
    <h2>Willkommen im Näh-Paradies!</h2>
    <p>Hier finden Sie Mützen, Schals, und weiter kleine Näharbeiten die alle Individuell und nur nach Anfrage gefertigt werden</p>

    <ImageCarousel />

    {/* Buttons, falls Sie sie unter dem Karussell behalten möchten */}
    {/* <Link to="/shop" className="nav-button">Zum Shop</Link>
    <Link to="/blog" className="nav-button" style={{ marginLeft: '10px' }}>Zum Blog</Link> */}
  </div>
);


const BlogPage = () => (
  <div style={{ padding: '20px' }}>
    <h2>✂️ Andreas Nähtipps & Blog</h2>
    <p>Neueste Anleitungen und Inspirationen für kreative Nähprojekte.</p>
  </div>
);

// --- Haupt-App-Layout ---
function App() {
  return (
    <div className="App">

      <nav>
        <Link to="/">Home</Link>
        <Link to="/shop">Shop</Link>
        <Link to="/blog">Blog</Link>
      </nav>

      <main>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/shop" element={<Shop />} />
          <Route path="/blog" element={<BlogPage />} />
          {/* Route für das Admin Dashboard (jetzt mit der echten Komponente) */}
          <Route path="/admin" element={<AdminDashboard />} />
          <Route path="*" element={<h2 style={{padding: '20px'}}>404 - Seite nicht gefunden</h2>} />
        </Routes>
      </main>

      <footer className="App-footer">
        © 2025 Henkes-Stoffzauber.de
      </footer>
    </div>
  );
}

export default App;
