// client/src/components/AdminDashboard.js
import React, { useState, useEffect, useCallback } from 'react';
import { LogOut, Edit, Trash2 } from 'lucide-react';

const API_URL = 'http://localhost:3001/api';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/100x100/CCCCCC/333333?text=N/A";

const getAuthHeaders = () => {
  const token = localStorage.getItem('adminToken');
  return token ? { Authorization: `Bearer ${token}` } : {};
};

const getImageUrl = (imagePath) => {
  if (imagePath && imagePath.startsWith('/uploads/')) {
    return `${BACKEND_URL}${imagePath}`;
  }
  return imagePath || FALLBACK_IMAGE_URL;
};

// ===================== PRODUKT FORMULAR =====================
const ProductForm = ({ productToEdit, onSubmit, onCancel, fabrics }) => {
  const [formData, setFormData] = useState(productToEdit || {
    name: '',
    description: '',
    price: 0,
    stock: 0,
    size: '',
    isFeatured: false,
    fabricId: '',
    imageFile: null
  });

  useEffect(() => {
    setFormData(productToEdit || {
      name: '',
      description: '',
      price: 0,
      stock: 0,
      size: '',
      isFeatured: false,
      fabricId: '',
      imageFile: null
    });
  }, [productToEdit]);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (name === 'imageFile') {
      setFormData({ ...formData, imageFile: files[0] });
    } else if (type === 'checkbox') {
      setFormData({ ...formData, [name]: checked });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h2 className="form-section-title">
        {productToEdit ? "Produkt bearbeiten" : "Neues Produkt erstellen"}
      </h2>

      <div className="form-group">
        <label>Produktname</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required />
      </div>

      <div className="form-group">
        <label>Beschreibung</label>
        <textarea name="description" value={formData.description} onChange={handleChange} required />
      </div>

      <div className="form-group">
        <label>Preis (€)</label>
        <input type="number" name="price" value={formData.price} onChange={handleChange} min="0" step="0.01" required />
      </div>

      <div className="form-group">
        <label>Bestand</label>
        <input type="number" name="stock" value={formData.stock} onChange={handleChange} min="0" required />
      </div>

      <div className="form-group">
        <label>Größe / Maß</label>
        <input type="text" name="size" value={formData.size} onChange={handleChange} placeholder="z.B. 56cm, M, L" required />
      </div>

      <div className="form-group">
        <label>Stoff auswählen</label>
        <select name="fabricId" value={formData.fabricId} onChange={handleChange}>
          <option value="">-- Stoff wählen --</option>
          {fabrics.map(f => <option key={f.id} value={f.id}>{f.name}</option>)}
        </select>
      </div>

      <div className="form-group checkbox-group">
        <label>
          <input type="checkbox" name="isFeatured" checked={formData.isFeatured} onChange={handleChange} /> Als Angebot anzeigen
        </label>
      </div>

      <div className="form-group">
        <label>Bild</label>
        <input type="file" name="imageFile" onChange={handleChange} accept="image/*" />
      </div>

      <div className="form-button-row">
        <button type="submit">Speichern</button>
        {productToEdit && (
          <button type="button" onClick={onCancel}>Abbrechen</button>
        )}
      </div>
    </form>
  );
};

// ===================== STOFF FORMULAR =====================
const FabricForm = ({ fabricToEdit, onSubmit, onCancel }) => {
  const [formData, setFormData] = useState(fabricToEdit || {
    name: '',
    fabricType: '',
    stock: 0,
    unit: 'cm',
    imageFile: null
  });

  useEffect(() => {
    setFormData(fabricToEdit || {
      name: '',
      fabricType: '',
      stock: 0,
      unit: 'cm',
      imageFile: null
    });
  }, [fabricToEdit]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'imageFile') {
      setFormData({ ...formData, imageFile: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h2 className="form-section-title">
        {fabricToEdit ? "Stoff bearbeiten" : "Neuen Stoff hinzufügen"}
      </h2>

      <div className="form-group">
        <label>Stoffname</label>
        <input name="name" value={formData.name} onChange={handleChange} required />
      </div>

      <div className="form-group">
        <label>Kategorie</label>
        <input name="fabricType" value={formData.fabricType} onChange={handleChange} required />
      </div>

      <div className="form-group">
        <label>Bestand</label>
        <input name="stock" type="number" min="0" value={formData.stock} onChange={handleChange} required />
      </div>

      <div className="form-group">
        <label>Einheit</label>
        <select name="unit" value={formData.unit} onChange={handleChange}>
          <option value="cm">cm</option>
          <option value="m">m</option>
        </select>
      </div>

      <div className="form-group">
        <label>Bild</label>
        <input type="file" name="imageFile" onChange={handleChange} accept="image/*" />
      </div>

      <div className="form-button-row">
        <button type="submit">Speichern</button>
        {fabricToEdit && <button type="button" onClick={onCancel}>Abbrechen</button>}
      </div>
    </form>
  );
};

// ===================== ADMIN DASHBOARD =====================
const AdminDashboard = ({ onLogout }) => {
  const [products, setProducts] = useState([]);
  const [fabrics, setFabrics] = useState([]);
  const [currentProduct, setCurrentProduct] = useState(null);
  const [currentFabric, setCurrentFabric] = useState(null);
  const [message, setMessage] = useState("");
  const [productSearch, setProductSearch] = useState("");
  const [fabricSearch, setFabricSearch] = useState("");

  const fetchAll = useCallback(async () => {
    try {
      const prodRes = await fetch(`${API_URL}/products`, { headers: getAuthHeaders() });
      const fabRes = await fetch(`${API_URL}/fabrics`, { headers: getAuthHeaders() });

      if (!prodRes.ok || !fabRes.ok) throw new Error("Fehler beim Laden");

      setProducts(await prodRes.json());
      setFabrics(await fabRes.json());
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  }, []);

  useEffect(() => { fetchAll(); }, [fetchAll]);

  // ===== Produkt speichern =====
  const handleProductSubmit = async (data) => {
    try {
      const formData = new FormData();
      Object.keys(data).forEach(key => {
        if (data[key] !== null) formData.append(key, data[key]);
      });

      const method = data.id ? "PUT" : "POST";
      const url = data.id ? `${API_URL}/products/${data.id}` : `${API_URL}/products`;

      const res = await fetch(url, { method, headers: getAuthHeaders(), body: formData });
      if (!res.ok) throw new Error("Fehler beim Speichern");
      setMessage("✅ Produkt gespeichert");
      setCurrentProduct(null);
      fetchAll();
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  };

  // ===== Produkt löschen =====
  const handleDeleteProduct = async (id) => {
    if (!window.confirm("Produkt wirklich löschen?")) return;
    try {
      const res = await fetch(`${API_URL}/products/${id}`, { method: "DELETE", headers: getAuthHeaders() });
      if (!res.ok) throw new Error("Fehler beim Löschen");
      setMessage("✅ Produkt gelöscht");
      fetchAll();
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  };

  // ===== Stoff speichern =====
  const handleSaveFabric = async (data) => {
    try {
      const formData = new FormData();
      Object.keys(data).forEach(key => {
        if (data[key] !== null) formData.append(key, data[key]);
      });

      const method = data.id ? "PUT" : "POST";
      const url = data.id ? `${API_URL}/fabrics/${data.id}` : `${API_URL}/fabrics`;

      const res = await fetch(url, { method, headers: getAuthHeaders(), body: formData });
      if (!res.ok) throw new Error("Fehler beim Speichern");
      setMessage("✅ Stoff gespeichert");
      setCurrentFabric(null);
      fetchAll();
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  };

  // ===== Stoff löschen =====
  const handleDeleteFabric = async (id) => {
    if (!window.confirm("Stoff wirklich löschen?")) return;
    try {
      const res = await fetch(`${API_URL}/fabrics/${id}`, { method: "DELETE", headers: getAuthHeaders() });
      if (!res.ok) throw new Error("Fehler beim Löschen");
      setMessage("✅ Stoff gelöscht");
      fetchAll();
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  };

  // ===== Filter Listen =====
  const filteredProducts = products.filter(p =>
    p.name.toLowerCase().includes(productSearch.toLowerCase())
  );

  const filteredFabrics = fabrics.filter(f =>
    f.name.toLowerCase().includes(fabricSearch.toLowerCase()) ||
    f.fabricType.toLowerCase().includes(fabricSearch.toLowerCase())
  );

  return (
    <div className="admin-dashboard-container">
      <h1>Admin Dashboard</h1>

      <button className="logout-button" onClick={() => { localStorage.removeItem('adminToken'); onLogout(); }}>
        <LogOut /> Logout
      </button>

      {message && (
        <p className={`status-message ${message.includes("❌") ? "status-message-error" : "status-message-success"}`}>
          {message}
        </p>
      )}

      {/* ===================== PRODUKTE ===================== */}
      <div className="admin-dashboard-grid">
        <ProductForm
          productToEdit={currentProduct}
          onSubmit={handleProductSubmit}
          onCancel={() => setCurrentProduct(null)}
          fabrics={fabrics}
        />

        <div className="admin-list-container">
          <h3>Produkte ({filteredProducts.length})</h3>
          <input
            type="text"
            placeholder="Produkte suchen..."
            className="search-input"
            value={productSearch}
            onChange={(e) => setProductSearch(e.target.value)}
          />
          {filteredProducts.map(p => (
            <div key={p.id} className="admin-list-item">
              <img src={getImageUrl(p.imageUrl)} alt="" />
              <span>{p.name}</span>
              <button onClick={() => setCurrentProduct(p)}><Edit /></button>
              <button onClick={() => handleDeleteProduct(p.id)}><Trash2 /></button>
            </div>
          ))}
        </div>
      </div>

      {/* ===================== STOFFE ===================== */}
      <div className="admin-dashboard-grid" style={{ marginTop: '40px' }}>
        <FabricForm
          fabricToEdit={currentFabric}
          onSubmit={handleSaveFabric}
          onCancel={() => setCurrentFabric(null)}
        />

        <div className="admin-list-container">
          <h3>Stoffe ({filteredFabrics.length})</h3>
          <input
            type="text"
            placeholder="Stoffe suchen..."
            className="search-input"
            value={fabricSearch}
            onChange={(e) => setFabricSearch(e.target.value)}
          />
          {filteredFabrics.map(f => (
            <div key={f.id} className="admin-list-item">
              <img src={getImageUrl(f.imageUrl)} alt="" />
              <span>{f.name} – {f.stock}{f.unit}</span>
              <button onClick={() => setCurrentFabric(f)}><Edit /></button>
              <button onClick={() => handleDeleteFabric(f.id)}><Trash2 /></button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
