// src/components/Checkout.js
import React, { useState, useEffect, useRef } from 'react';
import { useCart } from './CartContext';
import './Checkout.css';

const Checkout = () => {
  const { cartItems, clearCart } = useCart();
  const [formData, setFormData] = useState({
    firstName: '', lastName: '', email: '', phone: '',
    street: '', houseNumber: '', city: '', zip: '',
    paymentMethod: 'paypal'
  });
  const [paypalClientId, setPaypalClientId] = useState(null);
  const [loadingPaypal, setLoadingPaypal] = useState(false);
  const [invoiceLink, setInvoiceLink] = useState('');
  const paypalRef = useRef(null);

  const totalPrice = cartItems.reduce((sum, item) => sum + Number(item.price || 0) * Number(item.quantity || 0), 0);

  useEffect(() => {
    // load config (paypal client id)
    fetch('http://localhost:3001/api/config')
      .then(r => r.json())
      .then(data => setPaypalClientId(data.paypalClientId || null))
      .catch(err => {
        console.warn('Could not load config:', err);
        setPaypalClientId(null);
      });
  }, []);

  useEffect(() => {
    if (formData.paymentMethod !== 'paypal') return;
    if (!paypalClientId) return;

    // ensure no double script
    const existing = document.getElementById('paypal-sdk');
    if (existing) existing.remove();

    setLoadingPaypal(true);
    const s = document.createElement('script');
    s.id = 'paypal-sdk';
    s.src = `https://www.paypal.com/sdk/js?client-id=${paypalClientId}&currency=EUR`;
    s.async = true;
    s.onload = () => {
      setLoadingPaypal(false);
      if (!window.paypal) return;
      // render buttons
      try {
        window.paypal.Buttons({
          style: { layout: 'vertical', color: 'gold', shape: 'pill', label: 'pay' },

          createOrder: async () => {
            // create order on server -> returns paypalOrderId
            const res = await fetch('http://localhost:3001/api/checkout', {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({ cart: cartItems, address: formData, paymentMethod: 'paypal' })
            });
            const data = await res.json();
            if (!data || !data.paypalOrderId) throw new Error('Konnte PayPal-Order nicht erstellen');
            // store invoice link if returned later
            if (data.invoicePath) setInvoiceLink(`http://localhost:3001/${data.invoicePath}`);
            return data.paypalOrderId;
          },

          onApprove: async (data, actions) => {
            // client-side capture (works) -> then inform server to finalize (mark paid and send invoice)
            try {
              await actions.order.capture();
            } catch (err) {
              console.error('Client capture error', err);
            }

            // call server confirm to finalize (server captures as well to be safe / generate invoice)
            await fetch('http://localhost:3001/api/checkout/confirm', {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({ paypalOrderId: data.orderID })
            });

            alert(`Vielen Dank f√ºr Ihre Bestellung, ${formData.firstName}! üíñ`);
            clearCart();
          },

          onError: (err) => {
            console.error('PayPal error', err);
            alert('PayPal-Zahlung fehlgeschlagen.');
          }

        }).render(paypalRef.current);
      } catch (err) {
        console.error('PayPal render error', err);
      }
    };
    s.onerror = () => {
      setLoadingPaypal(false);
      console.error('PayPal SDK konnte nicht geladen werden');
    };

    document.body.appendChild(s);
    return () => {
      const el = document.getElementById('paypal-sdk');
      if (el) el.remove();
      if (paypalRef.current) paypalRef.current.innerHTML = '';
    };
  }, [paypalClientId, formData.paymentMethod, cartItems, formData, clearCart]);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.paymentMethod === 'paypal') return; // PayPal uses its own button process

    // bank or cash: create order and receive invoice
    const res = await fetch('http://localhost:3001/api/checkout', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ cart: cartItems, address: formData, paymentMethod: formData.paymentMethod })
    });
    const data = await res.json();
    if (data && data.success) {
      if (data.invoicePath) setInvoiceLink(`http://localhost:3001/${data.invoicePath}`);
      alert('Vielen Dank f√ºr Ihre Bestellung! üíñ');
      clearCart();
    } else {
      alert('Fehler bei der Bestellung. Bitte versuche es sp√§ter.');
    }
  };

  return (
    <div className="checkout-container">
      <h2>Zur Kasse ‚ú®</h2>
      {cartItems.length === 0 ? (
        <p>Dein Warenkorb ist leer</p>
      ) : (
        <>
          <p><strong>Gesamtbetrag: ‚Ç¨ {totalPrice.toFixed(2)}</strong></p>
          <form className="checkout-form" onSubmit={handleSubmit}>
            <label>Vorname</label>
            <input name="firstName" value={formData.firstName} onChange={handleChange} required />

            <label>Nachname</label>
            <input name="lastName" value={formData.lastName} onChange={handleChange} required />

            <label>Email</label>
            <input name="email" type="email" value={formData.email} onChange={handleChange} required />

            <label>Telefon</label>
            <input name="phone" value={formData.phone} onChange={handleChange} required />

            <label>Stra√üe</label>
            <input name="street" value={formData.street} onChange={handleChange} required />

            <label>Hausnummer</label>
            <input name="houseNumber" value={formData.houseNumber} onChange={handleChange} required />

            <label>Stadt</label>
            <input name="city" value={formData.city} onChange={handleChange} required />

            <label>Postleitzahl</label>
            <input name="zip" value={formData.zip} onChange={handleChange} required />

            <label>Zahlungsmethode</label>
            <select name="paymentMethod" value={formData.paymentMethod} onChange={handleChange}>
              <option value="paypal">PayPal</option>
              <option value="bank">√úberweisung</option>
              <option value="cash">Barzahlung</option>
            </select>

            {formData.paymentMethod !== 'paypal' && (
              <div className="checkout-buttons">
                <button type="submit">Jetzt bestellen</button>
                <button type="button" onClick={clearCart}>Alles l√∂schen</button>
              </div>
            )}
          </form>

          {formData.paymentMethod === 'paypal' && (
            <>
              {!paypalClientId ? (
                <p style={{ color: 'red' }}>PayPal nicht verf√ºgbar ‚Äì Client-ID fehlt.</p>
              ) : loadingPaypal ? (
                <p>PayPal wird geladen‚Ä¶</p>
              ) : (
                <div ref={paypalRef} className="paypal-button-container" />
              )}
            </>
          )}

          {invoiceLink && (
            <div style={{ marginTop: 18 }}>
              <a className="checkout-download" href={invoiceLink} download>PDF-Rechnung herunterladen</a>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Checkout;
