// client/src/components/ProductForm.js
import React, { useState, useEffect, useMemo } from 'react';

// Verwendung von useMemo, um ESLint-Warnung (exhaustive-deps) zu beheben
const getInitialState = (productToEdit) => productToEdit || {
    name: '',
    description: '',
    price: 0,
    stock: 0, 
    isFeatured: false,
    // NEUES FELD: String zur Speicherung der verfügbaren Stoffe (z.B. "Baumwolle, Jersey")
    availableFabrics: '', 
    imageFile: null,
    imageUrl: '', 
};

/**
 * Formular zum Erstellen oder Bearbeiten eines Produkts.
 * @param {object} props
 * @param {object|null} props.productToEdit - Das zu bearbeitende Produkt oder null für ein neues Produkt.
 * @param {function} props.onSubmit - Callback, wenn das Formular gesendet wird.
 * @param {function} props.onCancel - Callback, um den Bearbeitungsmodus abzubrechen.
 */
const ProductForm = ({ productToEdit, onSubmit, onCancel }) => {
    
    // Initialer Zustand wird einmal berechnet, wenn productToEdit sich ändert
    const initialState = useMemo(() => getInitialState(productToEdit), [productToEdit]);
    
    const [formData, setFormData] = useState(initialState);
    const [isEditing, setIsEditing] = useState(!!productToEdit);
    const [previewImage, setPreviewImage] = useState(formData.imageUrl || null);


    useEffect(() => {
        // Setzt das Formular zurück, wenn ein neues oder kein Produkt zum Bearbeiten übergeben wird
        setFormData(initialState);
        setIsEditing(!!productToEdit);
        setPreviewImage(initialState.imageUrl || null);
    }, [initialState, productToEdit]); 
    

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        
        setFormData(prev => ({
            ...prev,
            imageFile: file, // Die eigentliche Datei
            imageUrl: '', // Vorhandene URL löschen, da eine neue Datei ausgewählt wurde
        }));
        
        // Lokale URL für die Vorschau erstellen
        if (file) {
            setPreviewImage(URL.createObjectURL(file));
        } else {
            setPreviewImage(null);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // HINWEIS: alert() sollte im Immersive ersetzt werden, aber für eine einfache Validierung belassen wir es hier.
        if (!formData.name || formData.price <= 0 || formData.stock < 0) {
            alert("Bitte füllen Sie alle erforderlichen Felder korrekt aus (Name, Preis > 0, Bestand >= 0).");
            return;
        }

        // Ruft die onSubmit-Funktion des Dashboards auf
        onSubmit(formData, isEditing);
        
        // Das Formular wird vom Dashboard nach erfolgreichem Speichern zurückgesetzt
    };
    
    // Hilfsfunktion zur Generierung der korrekten Bild-URL (da der Kontext diese Funktion in AdminDashboard hat)
    const getProductImageUrl = (imagePath) => {
        if (imagePath && imagePath.startsWith('/uploads/')) {
            // Dies sollte nur passieren, wenn ein bestehendes Produkt geladen wird
            return `http://localhost:3001${imagePath}`;
        }
        return imagePath;
    };


    return (
        <form onSubmit={handleSubmit} className="admin-form">
            <h2 className="admin-form-title">
                {isEditing ? `Produkt bearbeiten: ${productToEdit.name}` : 'Neues Produkt hinzufügen'}
            </h2>
            
            {/* Produktname */}
            <div className="admin-form-group">
                <label htmlFor="name">Name:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />
            </div>

            {/* Beschreibung */}
            <div className="admin-form-group">
                <label htmlFor="description">Beschreibung:</label>
                <textarea
                    id="description"
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    rows="4"
                    required
                ></textarea>
            </div>
            
            {/* NEUES FELD: Verfügbare Stoffe */}
            <div className="admin-form-group">
                <label htmlFor="availableFabrics">Verfügbare Stoffe (Komma-getrennt):</label>
                <input
                    type="text"
                    id="availableFabrics"
                    name="availableFabrics"
                    value={formData.availableFabrics}
                    onChange={handleChange}
                    placeholder="z.B. Jersey, Baumwolle, Leinen"
                />
                <p className="admin-form-hint">
                    Geben Sie die Stoffe an, aus denen dieses Produkt hergestellt werden kann.
                </p>
            </div>
            {/* Ende NEUES FELD */}

            {/* Preis */}
            <div className="admin-form-group half-width">
                <label htmlFor="price">Preis (€):</label>
                <input
                    type="number"
                    id="price"
                    name="price"
                    value={formData.price}
                    onChange={handleChange}
                    min="0.01"
                    step="0.01"
                    required
                />
            </div>

            {/* Bestand */}
            <div className="admin-form-group half-width">
                <label htmlFor="stock">Bestand (Anzahl):</label>
                <input
                    type="number"
                    id="stock"
                    name="stock"
                    value={formData.stock}
                    onChange={handleChange}
                    min="0"
                    step="1"
                    required
                />
            </div>

            {/* Bild-Upload */}
            <div className="admin-form-group">
                <label htmlFor="imageFile">Produktbild:</label>
                <input
                    type="file"
                    id="imageFile"
                    name="imageFile"
                    accept="image/*"
                    onChange={handleFileChange}
                    // Nur erforderlich, wenn ein neues Produkt erstellt wird
                    required={!isEditing && !formData.imageUrl} 
                />
                <p className="admin-form-hint">
                    {isEditing && formData.imageUrl && !formData.imageFile
                        ? 'Aktuelles Bild beibehalten. Neue Datei wählen, um zu ersetzen.'
                        : 'Wählen Sie eine neue Bilddatei aus.'
                    }
                </p>
                {/* Bild-Vorschau */}
                {previewImage && (
                    <div className="image-preview-container">
                        <img 
                            src={previewImage.startsWith('blob:') ? previewImage : getProductImageUrl(previewImage)} 
                            alt="Bild Vorschau"
                            className="image-preview"
                        />
                    </div>
                )}
            </div>
            
            {/* Featured Checkbox */}
            <div className="admin-form-group" style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <input
                    type="checkbox"
                    id="isFeatured"
                    name="isFeatured"
                    checked={formData.isFeatured}
                    onChange={handleChange}
                    style={{ width: '20px', height: '20px' }}
                />
                <label htmlFor="isFeatured" style={{ margin: 0, fontWeight: 'normal', cursor: 'pointer' }}>
                    Als "Featured Product" auf der Startseite anzeigen
                </label>
            </div>
            
            {/* Submit-Button */}
            <div style={{ display: 'flex', gap: '10px', marginTop: '20px' }}>
                <button type="submit" className="admin-form-submit-button">
                    {isEditing ? 'Änderungen speichern' : 'Produkt erstellen'}
                </button>
            </div>
            
            {/* Abbrechen-Button (nur beim Bearbeiten) */}
            {isEditing && (
                <div style={{ marginTop: '15px', paddingTop: '15px', borderTop: '1px solid #eee' }}>
                    <button type="button" onClick={onCancel} className="admin-form-submit-button" style={{backgroundColor: '#666'}}>
                        Abbrechen (Zurück zur Erstellung)
                    </button>
                </div>
            )}
        </form>
    );
};

export default ProductForm;
