// client/src/components/ImageCarousel.js (NEUE DATEI)

import React, { useState, useEffect } from 'react';
import '../index.css'; // Für allgemeine Farbvariablen und potenzielles Karussell-CSS

// Bilder importieren (Pfade müssen stimmen!)
import img1 from '../assets/carousel1.jpg';
import img2 from '../assets/carousel2.jpg';
import img3 from '../assets/carousel3.jpg';
import img4 from '../assets/carousel4.jpg';
import img5 from '../assets/carousel5.jpg';

const images = [img1, img2, img3, img4, img5]; // Array der Bilder

const ImageCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    // Automatischer Bildwechsel alle 4 Sekunden
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => 
        (prevIndex + 1) % images.length // Geht zum nächsten Bild, springt am Ende zurück zum ersten
      );
    }, 4000); 

    // Aufräumfunktion: Stoppt das Intervall, wenn die Komponente unmontiert wird
    return () => clearInterval(interval);
  }, []); // Leeres Array bedeutet, Effekt läuft nur einmal beim Mounten

  return (
    <div className="carousel-container">
      <img 
        src={images[currentIndex]} 
        alt={`Karussell Bild ${currentIndex + 1}`} 
        className="carousel-image" 
      />
      <div className="carousel-dots">
        {images.map((_, index) => (
          <span
            key={index}
            className={`dot ${currentIndex === index ? 'active' : ''}`}
            onClick={() => setCurrentIndex(index)} // Direkter Klick auf Punkt wechselt Bild
          ></span>
        ))}
      </div>
    </div>
  );
};

export default ImageCarousel;
