// src/components/Checkout.js
import React, { useState, useEffect, useRef } from 'react';
import { useCart } from './CartContext';
import './Checkout.css';

const SERVER = process.env.REACT_APP_API_URL || 'http://localhost:3001';

export default function Checkout() {
  const { cartItems, clearCart } = useCart();
  const [formData, setFormData] = useState({
    firstName: '', lastName: '', email: '', phone: '',
    street: '', houseNumber: '', city: '', zip: '',
    paymentMethod: 'paypal'
  });
  const [invoiceLink, setInvoiceLink] = useState('');
  const [paypalClientId, setPaypalClientId] = useState(null);
  const [paypalEnv, setPaypalEnv] = useState('sandbox');
  const [loadingPaypal, setLoadingPaypal] = useState(false);
  const paypalRef = useRef(null);

  const totalPrice = cartItems.reduce((sum, item) => sum + Number(item.price || 0) * (Number(item.quantity) || 1), 0);

  // Fetch PayPal config from server
  useEffect(() => {
    fetch(`${SERVER}/api/config`)
      .then(res => res.json())
      .then(data => {
        setPaypalClientId(data.paypalClientId || null);
        setPaypalEnv(data.paypalEnv || 'sandbox');
      })
      .catch(err => {
        console.warn('Could not load /api/config', err);
        setPaypalClientId(null);
      });
  }, []);

  // Render PayPal Buttons
  useEffect(() => {
    if (formData.paymentMethod !== 'paypal') return;
    if (!paypalClientId) return;
    if (!paypalRef.current) return;

    // Clean up previous buttons
    paypalRef.current.innerHTML = '';
    setLoadingPaypal(true);

    const script = document.createElement('script');
    script.src = `https://www.paypal.com/sdk/js?client-id=${paypalClientId}&currency=EUR`;
    script.async = true;

    script.onload = () => {
      setLoadingPaypal(false);
      if (!window.paypal) return console.error('window.paypal not available');

      try {
        window.paypal.Buttons({
          style: { layout: 'vertical', color: 'gold', shape: 'pill', label: 'pay' },

          createOrder: async () => {
            const res = await fetch(`${SERVER}/api/checkout`, {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({ cart: cartItems, address: formData, paymentMethod: 'paypal' })
            });
            const data = await res.json();
            if (!data || !data.success) throw new Error(data?.error || 'Checkout creation failed');
            if (data.invoicePath) setInvoiceLink(`${SERVER}/${data.invoicePath}`);
            return data.paypalOrderId;
          },

          onApprove: async (data, actions) => {
            if (actions && actions.order && typeof actions.order.capture === 'function') {
              await actions.order.capture();
            }
            alert(`Vielen Dank fÃ¼r deine Bestellung, ${formData.firstName}!`);
            clearCart();
          },

          onError: (err) => {
            console.error('PayPal error', err);
            alert('PayPal-Zahlung fehlgeschlagen.');
          }

        }).render(paypalRef.current);
      } catch (err) {
        console.error('Error rendering PayPal Buttons', err);
      }
    };

    script.onerror = () => {
      setLoadingPaypal(false);
      console.error('PayPal SDK load error');
    };

    document.body.appendChild(script);

    return () => {
      if (paypalRef.current) paypalRef.current.innerHTML = '';
      script.remove();
    };
  }, [paypalClientId, formData.paymentMethod, cartItems, formData, clearCart]);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.paymentMethod === 'paypal') return;

    const res = await fetch(`${SERVER}/api/checkout`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ cart: cartItems, address: formData, paymentMethod: formData.paymentMethod })
    });
    const data = await res.json();
    if (data?.success) {
      if (data.invoicePath) setInvoiceLink(`${SERVER}/${data.invoicePath}`);
      alert('Bestellung erfolgreich! Danke ðŸ’–');
      clearCart();
    } else {
      alert('Fehler bei der Bestellung. Bitte prÃ¼fe die Eingaben oder Server-Logs.');
    }
  };

  return (
    <div className="checkout-container">
      <h2>Zur Kasse âœ¨</h2>

      {cartItems.length === 0 ? (
        <p>Dein Warenkorb ist leer</p>
      ) : (
        <>
          <p><strong>Gesamtbetrag: â‚¬ {totalPrice.toFixed(2)}</strong></p>

          <form className="checkout-form" onSubmit={handleSubmit}>
            <label>Vorname</label>
            <input name="firstName" value={formData.firstName} onChange={handleChange} required />
            <label>Nachname</label>
            <input name="lastName" value={formData.lastName} onChange={handleChange} required />
            <label>Email</label>
            <input name="email" type="email" value={formData.email} onChange={handleChange} required />
            <label>Telefon</label>
            <input name="phone" value={formData.phone} onChange={handleChange} />
            <label>StraÃŸe</label>
            <input name="street" value={formData.street} onChange={handleChange} required />
            <label>Hausnummer</label>
            <input name="houseNumber" value={formData.houseNumber} onChange={handleChange} required />
            <label>Stadt</label>
            <input name="city" value={formData.city} onChange={handleChange} required />
            <label>Postleitzahl</label>
            <input name="zip" value={formData.zip} onChange={handleChange} required />
            <label>Zahlungsmethode</label>
            <select name="paymentMethod" value={formData.paymentMethod} onChange={handleChange}>
              <option value="paypal">PayPal</option>
              <option value="bank">Ãœberweisung</option>
              <option value="cash">Barzahlung bei Abholung</option>
            </select>

            {formData.paymentMethod !== 'paypal' && (
              <div className="checkout-buttons">
                <button type="submit">Jetzt bestellen</button>
                <button type="button" onClick={() => clearCart()}>Alles lÃ¶schen</button>
              </div>
            )}
          </form>

          {formData.paymentMethod === 'paypal' && (
            <div style={{ marginTop: 16 }}>
              {loadingPaypal && <p>PayPal lÃ¤dtâ€¦</p>}
              {!loadingPaypal && paypalClientId && <div ref={paypalRef} className="paypal-button-container" />}
              {!paypalClientId && <p style={{ color: 'red' }}>PayPal nicht verfÃ¼gbar â€” Client-ID fehlt.</p>}
            </div>
          )}

          {invoiceLink && (
            <div style={{ marginTop: 16 }}>
              <a className="checkout-download" href={invoiceLink} download>
                ðŸ“„ Rechnung herunterladen
              </a>
            </div>
          )}
        </>
      )}
    </div>
  );
}
