// src/components/ProductDetail.js
import React, { useState, useEffect } from 'react';
import { useCart } from './CartContext';
import { useNavigate } from 'react-router-dom';

const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/500x500/CCCCCC/333333?text=Produkt";

const getAuthHeaders = () => {
  const token = localStorage.getItem('adminToken');
  return token ? { Authorization: `Bearer ${token}` } : {};
};

const getImageUrl = (imagePath) => {
  if (imagePath && imagePath.startsWith('/uploads/')) return `${BACKEND_URL}${imagePath}`;
  return imagePath || FALLBACK_IMAGE_URL;
};

const ProductDetail = ({ productId, navigate }) => {
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);

  const { addToCart, cartItems } = useCart();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const res = await fetch(`${API_URL}/${productId}`, { headers: getAuthHeaders() });
        if (!res.ok) throw new Error('Produkt nicht gefunden');
        const data = await res.json();
        setProduct(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchProduct();
  }, [productId]);

  const handleAddToCart = () => {
    if (quantity > 0 && quantity <= product.stock) {
      addToCart(product, quantity);
    }
  };

  if (loading) return <p style={{ textAlign: 'center', padding: '50px' }}>Lade Produkt...</p>;
  if (error) return <p style={{ textAlign: 'center', color: 'red', padding: '50px' }}>{error}</p>;
  if (!product) return null;

  // Gesamtanzahl im Warenkorb
  const totalCartItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);

  return (
    <div style={{ maxWidth: '1200px', margin: '50px auto', padding: '0 20px', display: 'flex', flexWrap: 'wrap', gap: '40px' }}>
      
      {/* Produktbild */}
      <div style={{ flex: '1 1 500px', textAlign: 'center' }}>
        <img
          src={getImageUrl(product.imageUrl)}
          alt={product.name}
          style={{ width: '100%', maxWidth: '600px', borderRadius: '8px' }}
          onError={e => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }}
        />
      </div>

      {/* Produktdetails */}
      <div style={{ flex: '1 1 500px' }}>
        <h2 style={{ color: 'var(--farbe-primär)' }}>{product.name}</h2>
        <p style={{ fontWeight: 'bold', fontSize: '1.5em' }}>€ {product.price.toFixed(2)}</p>
        <p style={{ margin: '10px 0', color: product.stock > 0 ? 'green' : 'red', fontWeight:'bold' }}>
          {product.stock > 0 ? `Sofort verfügbar (${product.stock} Stück)` : 'Ausverkauft'}
        </p>
        <p>{product.description}</p>
        {product.fabrics && <p><strong>Stoff(e):</strong> {product.fabrics}</p>}

        {product.stock > 0 && (
          <div style={{ marginTop: '20px' }}>
            <label>
              Menge:
              <input
                type="number"
                min="1"
                max={product.stock}
                value={quantity}
                onChange={e => setQuantity(Math.min(Math.max(1, e.target.value), product.stock))}
                style={{ width: '60px', marginLeft: '10px', padding: '5px', borderRadius: '5px', border: '1px solid #ccc' }}
              />
            </label>
            <button
              onClick={handleAddToCart}
              style={{
                display: 'block',
                marginTop: '15px',
                backgroundColor: 'var(--farbe-primär)',
                color: 'var(--farbe-hell)',
                padding: '10px 20px',
                border: 'none',
                borderRadius: '8px',
                fontWeight: 'bold',
                cursor: 'pointer'
              }}
            >
              In den Warenkorb
            </button>
          </div>
        )}

        <button
          onClick={() => navigate('/shop')}
          style={{
            marginTop: '30px',
            backgroundColor: '#ccc',
            color: '#333',
            padding: '8px 15px',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer'
          }}
        >
          Zurück zum Shop
        </button>

        {/* Warenkorb-Info */}
        <div style={{ marginTop: '40px', padding: '15px', backgroundColor: '#f5f5f5', borderRadius: '8px' }}>
          <h3>Warenkorb</h3>
          <p>Produkte im Warenkorb: {totalCartItems}</p>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
