// client/src/components/AdminDashboard.js
import React, { useState, useEffect } from 'react';
import ProductForm from './ProductForm'; // Import bleibt bestehen

const API_URL = 'http://localhost:3001/api/products';
const FALLBACK_IMAGE = "https://placehold.co/50x50/eee/333?text=Bild";


// FIX: Diese Funktion stellt sicher, dass /uploads/ Pfade die Server-URL erhalten
const getImageUrl = (imagePath) => {
// ... (Restlicher Code von getImageUrl bleibt unverändert) ...
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `http://localhost:3001${imagePath}`;
    }
    // Handhabt externe URLs (wie placehold.co) oder defekte Pfade
    return imagePath;
};


// --- Komponente: Produktliste ---
const AdminProductList = ({ products, onEdit, onDelete }) => {
    
    return (
// ... (Restlicher Code von AdminProductList bleibt unverändert) ...
        // Verwendet jetzt CSS-Klassen aus index.css
        <div className="admin-list-container">
            <h2 style={{ fontSize: '1.5em', color: '#333', margin: '0 0 20px 0' }}>
                Alle Produkte im System ({products.length})
            </h2>
            {products.length === 0 ? (
                <p style={{ color: '#666' }}>Keine Produkte gefunden.</p>
            ) : (
                <table className="admin-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Bild</th>
                            <th>Name</th>
                            <th>Preis</th>
                            <th>Featured</th>
                            <th>Bestand</th> {/* FIX: "Verfügbarkeit fehlt" behoben */}
                            <th>Aktionen</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map(product => (
                            <tr key={product.id}>
                                <td>{product.id}</td>
                                <td>
                                    <img 
                                        // FIX: getImageUrl wird hier korrekt verwendet
                                        src={getImageUrl(product.imageUrl) || FALLBACK_IMAGE} 
                                        alt={product.name} 
                                        style={{ width: '50px', height: '50px', objectFit: 'cover', borderRadius: '4px' }}
                                        onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE; }}
                                    />
                                </td>
                                <td>{product.name}</td>
                                <td>€ {product.price ? product.price.toFixed(2) : '0.00'}</td>
                                <td>{product.isFeatured ? 'Ja' : 'Nein'}</td>
                                <td>{product.stock}</td> {/* FIX: "Verfügbarkeit fehlt" behoben */}
                                <td>
                                    <button 
                                        className="admin-button admin-button-edit"
                                        onClick={() => onEdit(product)}
                                    >
                                        Bearbeiten
                                    </button>
                                    <button 
                                        className="admin-button admin-button-delete"
                                        onClick={() => onDelete(product.id, product.name)} // Name für Bestätigung übergeben
                                    >
                                        Löschen
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};
// ... (Restlicher Code von AdminDashboard (Hauptkomponente) bleibt unverändert) ...

// --- Hauptkomponente: Admin Dashboard ---
const AdminDashboard = () => {
    const [products, setProducts] = useState([]);
    const [currentProduct, setCurrentProduct] = useState(null); 
    const [statusMessage, setStatusMessage] = useState({ text: '', type: '' });
    
    // Daten vom Backend laden
    const fetchProducts = async () => {
        try {
            const response = await fetch(API_URL);
            if (!response.ok) throw new Error('Fehler beim Laden der Produkte.');
            const data = await response.json();
            setProducts(data);
        } catch (e) {
            console.error("Fetch error:", e);
            setStatusMessage({ text: 'Ladefehler: ' + e.message, type: 'error' });
        }
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    // Statusmeldung anzeigen
    const showStatus = (text, type = 'success') => {
        setStatusMessage({ text, type });
        setTimeout(() => setStatusMessage({ text: '', type: '' }), 5000);
    };

    // Produkt erstellen/aktualisieren
    const handleProductSubmit = async (formData, isEditing) => {
        const method = isEditing ? 'PUT' : 'POST';
        const url = isEditing ? `${API_URL}/${currentProduct.id}` : API_URL;

        try {
            const response = await fetch(url, {
                method: method,
                body: formData, 
            });

            if (!response.ok) {
                const errorData = await response.json();
                let errorMsg = `Serverfehler: ${response.status}`;
                if (errorData.error && errorData.error.includes('notNull Violation')) {
                    errorMsg = "Fehler: Alle Felder (Name, Beschreibung, Preis) und ein Bild sind erforderlich.";
                } else if (errorData.message) {
                    errorMsg = errorData.message;
                }
                throw new Error(errorMsg);
            }

            const message = isEditing ? 'Produkt erfolgreich aktualisiert.' : 'Produkt erfolgreich erstellt.';
            showStatus(message, 'success');
            setCurrentProduct(null); 
            fetchProducts(); 

        } catch (e) {
            console.error("Submission error:", e);
            showStatus(e.message, 'error');
        }
    };
    
    // Produkt löschen
    const handleProductDelete = async (id, name) => {
        // HINWEIS: window.confirm wird hier verwendet, da alert/confirm in der Sandbox nicht funktionieren.
        // In einer echten App (wie Ihrer) sollte dies funktionieren.
        if (!window.confirm(`Sind Sie sicher, dass Sie "${name}" (ID: ${id}) löschen möchten?`)) return;

        try {
            const response = await fetch(`${API_URL}/${id}`, {
                method: 'DELETE',
            });

            if (!response.ok) {
                throw new Error('Fehler beim Löschen des Produkts.');
            }

            showStatus('Produkt erfolgreich gelöscht.', 'success');
            fetchProducts();
            if (currentProduct && currentProduct.id === id) {
                 setCurrentProduct(null);
            }

        } catch (e) {
            console.error("Deletion error:", e);
            showStatus('Löschfehler: ' + e.message, 'error');
        }
    };
    
    // Bearbeitungsmodus starten
    const handleEditClick = (product) => {
        setCurrentProduct(product);
        showStatus(`Bearbeite Produkt: ${product.name}`, 'info');
        // Optional: Zum Formular scrollen
        window.scrollTo({ top: 0, behavior: 'smooth' });
    };
    
    // Bearbeitungsmodus abbrechen
    const handleCancelEdit = () => {
        setCurrentProduct(null);
        setStatusMessage({ text: '', type: '' });
    };

    // Status-Nachrichten-Stil (bleibt inline, da dynamisch)
    const statusStyle = {
        padding: '10px',
        margin: '10px 0',
        borderRadius: '8px',
        textAlign: 'center',
        fontWeight: 'bold',
        color: 'white',
        backgroundColor: statusMessage.type === 'error' ? '#ac292b' : '#4caf50',
        display: statusMessage.text ? 'block' : 'none', 
    };

    return (
        // Verwendet jetzt CSS-Klassen aus index.css
        <div className="admin-dashboard-container">
            
            <div style={statusStyle}>{statusMessage.text}</div>

            {/* 1. Spalte: Produktformular */}
            <ProductForm
                key={currentProduct ? currentProduct.id : 'new'} 
                productToEdit={currentProduct}
                onSubmit={handleProductSubmit}
                onCancel={handleCancelEdit}
            />

            {/* 2. Spalte: Produktliste */}
            <AdminProductList 
                products={products}
                onEdit={handleEditClick}
                onDelete={handleProductDelete}
            />
        </div>
    );
};

export default AdminDashboard;
