// src/components/Shop.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from './CartContext';
import './Shop.css';

const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/CCCCCC/333333?text=Stoffmuster";

const getImageUrl = (imagePath) =>
  imagePath && imagePath.startsWith('/uploads/') ? `${BACKEND_URL}${imagePath}` : (imagePath || FALLBACK_IMAGE_URL);

const ProductCard = ({ product, onAdd }) => {
  // defensive numeric price
  const price = Number(product.price) || 0;
  const inStock = Number(product.stock) > 0;

  return (
    <div className="product-card">
      <Link to={`/shop/${product.id}`}>
        <img
          src={getImageUrl(product.imageUrl)}
          alt={product.name}
          onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }}
        />
      </Link>

      <h3 className="product-name">{product.name}</h3>
      <p className="price-tag">‚Ç¨ {price.toFixed(2)}</p>
      <p className={inStock ? 'instock' : 'outstock'}>
        {inStock ? 'Sofort verf√ºgbar' : 'Ausverkauft'}
      </p>

      <button
        className="heart-button"
        onClick={() => onAdd({ ...product, price })}
        disabled={!inStock}
      >
        {inStock ? 'In den Warenkorb' : 'Nicht lieferbar'}
      </button>
    </div>
  );
};

const Shop = () => {
  const { cartItems, addToCart, removeFromCart, clearCart } = useCart();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');
  const [cartVisible, setCartVisible] = useState(false);

  useEffect(() => {
    let mounted = true;
    fetch(API_URL)
      .then(async res => {
        if (!res.ok) throw new Error(`Network ${res.status}`);
        const data = await res.json();
        if (mounted) setProducts(data);
      })
      .catch(err => setError(err.message))
      .finally(() => mounted && setLoading(false));
    return () => { mounted = false; };
  }, []);

  const filtered = products.filter(p => p.name && p.name.toLowerCase().includes(search.toLowerCase()));
  const totalItems = cartItems.reduce((s, i) => s + i.quantity, 0);
  const totalPrice = cartItems.reduce((s, i) => s + (Number(i.price) || 0) * i.quantity, 0);

  if (loading) return <p className="loading">Shop wird geladen...</p>;
  if (error) return <p className="error">Fehler: {error}</p>;

  return (
    <div className="shop-container">
      <h1 className="shop-title">Schau dich um ‚ú®</h1>

      <input
        type="text"
        placeholder="Suche ..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        className="search-input"
      />

      {filtered.length === 0 ? (
        <p className="no-results">Kein Produkt gefunden.</p>
      ) : (
        <div className="product-grid">
          {filtered.map(p => <ProductCard key={p.id} product={p} onAdd={addToCart} />)}
        </div>
      )}

      <button className="open-cart-button" aria-label="Warenkorb" onClick={() => setCartVisible(v => !v)}>
        üõí {totalItems}
      </button>

      <aside className={`sticky-cart ${cartVisible ? 'visible' : ''}`}>
        <div className="cart-header">
          <h3>üõí Dein Warenkorb</h3>
          <button className="close-cart" onClick={() => setCartVisible(false)}>‚úï</button>
        </div>

        {cartItems.length === 0 ? (
          <p className="empty-cart">Dein Warenkorb ist leer.</p>
        ) : (
          <>
            <ul className="cart-list">
              {cartItems.map(item => {
                const price = Number(item.price) || 0;
                return (
                  <li key={item.id} className="cart-item">
                    <div className="cart-item-left">
                      <div className="cart-item-name">{item.name}</div>
                      <div className="cart-item-qty">Anzahl: {item.quantity}</div>
                    </div>
                    <div className="cart-item-right">
                      <div className="cart-item-price">‚Ç¨ {(price * item.quantity).toFixed(2)}</div>
                      <div className="cart-item-actions">
                        <button className="decrease-btn" onClick={() => removeFromCart(item.id, 1)}>‚àí</button>
                        <button className="remove-btn" onClick={() => removeFromCart(item.id)}>‚ùå</button>
                      </div>
                    </div>
                  </li>
                );
              })}
            </ul>

            <div className="cart-summary">
              <div>Gesamt: <strong>‚Ç¨ {totalPrice.toFixed(2)}</strong></div>
            </div>

            <div className="sticky-cart-actions">
              <Link to="/checkout" className="checkout-btn" onClick={() => setCartVisible(false)}>Zur Kasse</Link>
              <button className="clear-btn" onClick={() => clearCart()}>Alles l√∂schen</button>
            </div>
          </>
        )}
      </aside>
    </div>
  );
};

export default Shop;
