import React, { useState, useEffect, useRef } from 'react';
import { useCart } from './CartContext';
import './Checkout.css';

const Checkout = () => {
  const { cartItems, clearCart } = useCart();
  const [formData, setFormData] = useState({
    firstName: '', lastName: '', email: '', phone: '',
    street: '', houseNumber: '', city: '', zip: '',
    paymentMethod: 'paypal'
  });
  const [invoiceLink, setInvoiceLink] = useState('');
  const paypalRef = useRef(null);
  const totalPrice = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  // PayPal SDK laden
  useEffect(() => {
    if (formData.paymentMethod !== 'paypal') return;

    const script = document.createElement('script');
    script.src = `https://www.paypal.com/sdk/js?client-id=${process.env.REACT_APP_PAYPAL_CLIENT_ID}&currency=EUR`;
    script.addEventListener('load', () => {
      if (window.paypal) {
        window.paypal.Buttons({
          style: { layout: 'vertical', color: 'gold', shape: 'pill', label: 'pay' },

          createOrder: async (_, actions) => {
            const res = await fetch('http://localhost:3001/api/checkout', {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({ cart: cartItems, address: formData, paymentMethod: 'paypal' })
            });
            const data = await res.json();
            if (!data.success) throw new Error('Fehler beim Anlegen der Bestellung');
            if (data.invoicePath) setInvoiceLink(`http://localhost:3001/${data.invoicePath}`);
            return data.paypalOrderId || '';
          },

          onApprove: async (_, actions) => {
            const capture = await actions.order.capture();
            alert(`Vielen Dank f√ºr Ihre Bestellung, ${formData.firstName}! üíñ`);
            clearCart();
          },

          onError: (err) => {
            console.error(err);
            alert('PayPal-Zahlung fehlgeschlagen');
          }
        }).render(paypalRef.current);
      }
    });
    document.body.appendChild(script);
    return () => document.body.removeChild(script);
  }, [formData.paymentMethod, cartItems]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.paymentMethod === 'paypal') return;

    const res = await fetch('http://localhost:3001/api/checkout', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ cart: cartItems, address: formData, paymentMethod: formData.paymentMethod })
    });
    const data = await res.json();
    if (data.success) {
      if (data.invoicePath) setInvoiceLink(`http://localhost:3001/${data.invoicePath}`);
      alert('Vielen Dank f√ºr Ihre Bestellung! üíñ');
      clearCart();
    }
  };

  return (
    <div className="checkout-container">
      <h2>Zur Kasse ‚ú®</h2>
      {cartItems.length === 0 ? <p>Dein Warenkorb ist leer</p> : (
        <>
          <p><strong>Gesamtbetrag: ‚Ç¨ {totalPrice.toFixed(2)}</strong></p>
          <form className="checkout-form" onSubmit={handleSubmit}>
            <label>Vorname</label>
            <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} required />

            <label>Nachname</label>
            <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} required />

            <label>Email</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} required />

            <label>Telefon</label>
            <input type="tel" name="phone" value={formData.phone} onChange={handleChange} required />

            <label>Stra√üe</label>
            <input type="text" name="street" value={formData.street} onChange={handleChange} required />

            <label>Hausnummer</label>
            <input type="text" name="houseNumber" value={formData.houseNumber} onChange={handleChange} required />

            <label>Stadt</label>
            <input type="text" name="city" value={formData.city} onChange={handleChange} required />

            <label>Postleitzahl</label>
            <input type="text" name="zip" value={formData.zip} onChange={handleChange} required />

            <label>Zahlungsmethode</label>
            <select name="paymentMethod" value={formData.paymentMethod} onChange={handleChange}>
              <option value="paypal">PayPal</option>
              <option value="bank">√úberweisung</option>
              <option value="cash">Barzahlung</option>
            </select>

            {formData.paymentMethod !== 'paypal' && (
              <div className="checkout-buttons">
                <button type="submit">Jetzt bestellen</button>
                <button type="button" onClick={clearCart}>Alles l√∂schen</button>
              </div>
            )}
          </form>

          {formData.paymentMethod === 'paypal' && (
            <div ref={paypalRef} style={{ marginTop: '20px' }}></div>
          )}

          {invoiceLink && (
            <div style={{ marginTop: '20px', textAlign: 'center' }}>
              <a href={invoiceLink} download={`Rechnung_${formData.lastName}.pdf`} className="checkout-buttons">
                PDF-Rechnung herunterladen
              </a>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Checkout;
