import React from 'react';
import { useNavigate } from 'react-router-dom'; // useNavigate für Shop-Button

// Wir verwenden den Pfad "/logo.jpg" in der Annahme, dass das Bild im Public-Ordner liegt
// und so von der Build-Umgebung korrekt aufgelöst werden kann.
const logoImage = '/logo.jpg'; 

const Home = () => {
    
    const navigate = useNavigate(); // Hook für die Navigation

    // Funktion für den Klick auf den "Zum Shop"-Button
    const handleShopClick = (e) => {
        e.preventDefault();
        navigate('/shop');
    };

    // --- Styles für den Hauptinhalt ---\
    const containerStyle = {
        // Reduziert den oberen Margin, da der Banner fehlt
        maxWidth: '1000px',
        margin: '50px auto 50px auto', 
        padding: '40px',
        backgroundColor: 'var(--farbe-hell, #ffffff)', // Fallback-Farbe hinzugefügt
        borderRadius: '15px',
        boxShadow: '0 10px 30px rgba(0, 0, 0, 0.1)',
        textAlign: 'center',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center', // Logo zentrieren
    };

    // Style für das Logo im Hauptinhalt
    const logoStyle = {
        maxWidth: '350px', // Maximale Breite des Logos anpassen
        height: 'auto',
        margin: '0 auto 30px auto', // Abstand nach unten
        borderRadius: '10px',
        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
    };

    const textStyle = {
        color: 'var(--farbe-text, #333333)',
        fontSize: '1.1em',
        lineHeight: '1.6',
        marginBottom: '30px',
    };

    const buttonStyle = {
        padding: '12px 25px',
        fontSize: '1.1em',
        fontWeight: 'bold',
        backgroundColor: 'var(--farbe-akzent, #e57373)', // Akzentfarbe
        color: 'white',
        textDecoration: 'none',
        borderRadius: '8px',
        border: 'none',
        cursor: 'pointer',
        transition: 'background-color 0.3s ease',
    };

    return (
        <div style={containerStyle}>
            {/* Logo-Bereich: Einbau des Logos */}
            <div style={{ marginBottom: '40px' }}>
                <img 
                    // Das Logo im Hauptteil verwendet den korrekten Pfad
                    src={logoImage} 
                    alt="Henkes Stoffzauber Logo" 
                    style={logoStyle} 
                    // Fallback-Bild, falls /logo.jpg nicht gefunden wird
                    onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/350x150/6f42c1/ffffff?text=LOGO+FEHLT'; }}
                />
                
                <h1 style={{ color: 'var(--farbe-primär, #6f42c1)', marginBottom: '20px' }}>Willkommen bei Henkes Stoffzauber</h1>
                <p style={textStyle}>
                    Entdecken Sie unsere handverlesene Auswahl an hochwertigen Stoffen und kreativen Näh-Ideen. 
                    Hier finden Sie alles, was Ihr Nähherz begehrt – von Jersey bis Leder. Lassen Sie sich inspirieren 
                    und beginnen Sie Ihr nächstes Nähprojekt noch heute!
                </p>
                <a 
                    href="/shop" 
                    style={buttonStyle}
                    // Implementierung eines einfachen Hover-Effekts
                    onMouseEnter={(e) => e.target.style.backgroundColor = 'var(--farbe-sekundär)'} 
                    onMouseLeave={(e) => e.target.style.backgroundColor = 'var(--farbe-akzent)'}
                    onClick={handleShopClick}
                >
                    Zum Shop
                </a>
            </div>
        </div>
    );
};

export default Home;
