// src/components/Checkout.js
import React, { useState } from 'react';
import { useCart } from './CartContext';
import './Checkout.css';

const Checkout = () => {
  const { cartItems, clearCart } = useCart();
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    street: '',
    houseNumber: '',
    city: '',
    zip: '',
    paymentMethod: 'paypal',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrorMsg('');

    const orderData = {
      ...formData,
      cartItems,
      total: cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0),
    };

    try {
      const response = await fetch('http://localhost:3001/api/checkout', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(orderData),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Bestellung fehlgeschlagen');
      }

      alert('Vielen Dank f√ºr deine Bestellung! Eine Best√§tigung wurde an deine E-Mail gesendet üíñ');
      clearCart();
    } catch (error) {
      console.error(error);
      setErrorMsg('Fehler beim Abschicken der Bestellung. Bitte erneut versuchen.');
    } finally {
      setLoading(false);
    }
  };

  const totalPrice = cartItems.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  return (
    <div className="checkout-container">
      <h2>Zur Kasse ‚ú®</h2>

      {cartItems.length === 0 ? (
        <p>Dein Warenkorb ist leer</p>
      ) : (
        <>
          <p><strong>Gesamtbetrag: ‚Ç¨ {totalPrice.toFixed(2)}</strong></p>

          {errorMsg && <p className="error-msg">{errorMsg}</p>}

          <form className="checkout-form" onSubmit={handleSubmit}>
            <label>Vorname</label>
            <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} required />

            <label>Nachname</label>
            <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} required />

            <label>Email</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} required />

            <label>Telefon</label>
            <input type="tel" name="phone" value={formData.phone} onChange={handleChange} required />

            <label>Stra√üe</label>
            <input type="text" name="street" value={formData.street} onChange={handleChange} required />

            <label>Hausnummer</label>
            <input type="text" name="houseNumber" value={formData.houseNumber} onChange={handleChange} required />

            <label>Stadt</label>
            <input type="text" name="city" value={formData.city} onChange={handleChange} required />

            <label>Postleitzahl</label>
            <input type="text" name="zip" value={formData.zip} onChange={handleChange} required />

            <label>Zahlungsmethode</label>
            <select name="paymentMethod" value={formData.paymentMethod} onChange={handleChange}>
              <option value="paypal">PayPal</option>
              <option value="bank">√úberweisung</option>
              <option value="cash">Barzahlung bei Abholung</option>
            </select>

            <div className="checkout-buttons">
              <button type="submit" disabled={loading}>
                {loading ? 'Sende Bestellung...' : 'Jetzt bestellen'}
              </button>

              <button type="button" onClick={clearCart}>Alles l√∂schen</button>
            </div>
          </form>
        </>
      )}
    </div>
  );
};

export default Checkout;
