import React, { useState, useEffect } from 'react';
import { useCart } from './CartContext'; // KORRIGIERTER PFAD: Nun direkt aus dem Root-Verzeichnis

const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/500x500/F2B2B4/4F3C3C?text=Stoffmuster";

// Hilfsfunktion zur Generierung der korrekten Bild-URL
const getImageUrl = (imagePath) => {
    // Wenn der Pfad mit '/uploads/' beginnt, muss die Basis-URL hinzugefügt werden
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `${BACKEND_URL}${imagePath}`;
    }
    // Ansonsten ist es eine externe URL oder ein Fallback-Pfad
    return imagePath;
};


const ProductDetail = ({ productId, navigate }) => { // navigate hinzugefügt
    const { addToCart } = useCart(); // Warenkorb-Funktion
    
    const [product, setProduct] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [quantity, setQuantity] = useState(1); // Lokaler Zustand für die Menge
    const [message, setMessage] = useState(''); // Lokale Nachricht (z.B. "Zum Warenkorb hinzugefügt")

    useEffect(() => {
        if (!productId) {
            setError('Keine Produkt-ID in der URL angegeben.');
            setIsLoading(false);
            return;
        }
        
        const fetchProduct = async () => {
            try {
                const response = await fetch(`${API_URL}/${productId}`);
                if (!response.ok) {
                    throw new Error(`Produkt nicht gefunden oder Netzwerkfehler: ${response.status}`);
                }
                const data = await response.json();
                setProduct(data);
            } catch (e) {
                console.error("Fehler beim Abrufen des Produkts:", e);
                setError(e.message);
            } finally {
                setIsLoading(false);
            }
        };
        
        fetchProduct();

        // Timeout für die Nachricht
        let messageTimer;
        if (message) {
            messageTimer = setTimeout(() => setMessage(''), 3000);
        }
        return () => clearTimeout(messageTimer);
    }, [productId, message]); // message als Dependency, um den Timer bei neuer Nachricht zurückzusetzen


    const handleAddToCart = () => {
        if (product && quantity > 0 && quantity <= product.stock) {
            addToCart(product, quantity);
            setMessage(`${quantity}x "${product.name}" zum Warenkorb hinzugefügt!`);
        } else {
            setMessage('Fehler: Ungültige Menge.');
        }
    };

    if (isLoading) {
        return <p style={{ textAlign: 'center', padding: '50px' }}>Produkt wird geladen...</p>;
    }
    if (error) {
        return <p style={{ textAlign: 'center', color: 'var(--farbe-rot)', padding: '50px', fontWeight: 'bold' }}>Fehler: {error}</p>;
    }
    if (!product) {
        return <p style={{ textAlign: 'center', padding: '50px' }}>Produkt nicht gefunden.</p>;
    }

    const isInStock = product.stock > 0;

    // --- Inline Styles für Detailansicht ---
    const styles = {
        container: {
            maxWidth: '1000px',
            margin: '50px auto',
            padding: '30px',
            backgroundColor: 'var(--farbe-hell)',
            borderRadius: '15px',
            boxShadow: '0 8px 25px rgba(0, 0, 0, 0.1)',
        },
        detailGrid: {
            display: 'flex',
            gap: '40px',
            alignItems: 'flex-start',
            flexWrap: 'wrap',
        },
        imageBox: {
            flex: '1 1 400px',
            maxWidth: '45%',
            minWidth: '300px',
        },
        infoBox: {
            flex: '1 1 400px',
            maxWidth: '50%',
            minWidth: '300px',
        },
        productImage: {
            width: '100%',
            height: 'auto',
            borderRadius: '10px',
            boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
        },
        productTitle: {
            fontSize: '2.2em',
            color: 'var(--farbe-primär)',
            marginBottom: '10px',
        },
        productPrice: {
            fontSize: '1.8em',
            color: 'var(--farbe-akzent)',
            fontWeight: 'bold',
            marginBottom: '20px',
        },
        stockStatus: {
            fontSize: '1.1em',
            fontWeight: 'bold',
            color: isInStock ? '#4caf50' : '#f44336',
            marginBottom: '20px',
        },
        quantityInput: {
            padding: '10px',
            width: '80px',
            border: '1px solid #ccc',
            borderRadius: '8px',
            textAlign: 'center',
            marginRight: '10px',
        },
        addToCartButton: {
            padding: '12px 25px',
            backgroundColor: 'var(--farbe-akzent)',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: isInStock ? 'pointer' : 'not-allowed',
            fontWeight: 'bold',
            opacity: isInStock ? 1 : 0.6,
            transition: 'background-color 0.3s ease',
        },
        messageBox: {
            marginTop: '20px',
            padding: '10px',
            backgroundColor: '#d4edda',
            color: '#155724',
            borderRadius: '8px',
            fontWeight: 'bold',
            textAlign: 'center',
        },
        backButton: {
            marginBottom: '20px',
            display: 'inline-block',
            cursor: 'pointer',
            color: 'var(--farbe-primär)',
            fontWeight: 'bold',
        },
        // Neue Stile
        detailInfo: {
            fontSize: '1em',
            color: 'var(--farbe-text)',
            marginBottom: '10px',
        },
        detailHeading: {
            fontSize: '1.4em', 
            marginBottom: '10px', 
            color: 'var(--farbe-text)'
        }
    };


    return (
        <div style={styles.container}>
            <p onClick={() => navigate('/shop')} style={styles.backButton}>
                &larr; Zurück zum Shop
            </p>

            <div style={styles.detailGrid}>
                {/* Bild-Bereich */}
                <div style={styles.imageBox}>
                    <img 
                        src={getImageUrl(product.imageUrl)} 
                        alt={product.name} 
                        style={styles.productImage} 
                        onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }}
                    />
                </div>

                {/* Info-Bereich */}
                <div style={styles.infoBox}>
                    <h1 style={styles.productTitle}>{product.name}</h1>
                    
                    <p style={styles.productPrice}>€ {product.price.toFixed(2)}</p>
                    
                    <p style={styles.stockStatus}>
                        Status: {isInStock ? `Auf Lager (${product.stock} verfügbar)` : 'Ausverkauft'}
                    </p>

                    {/* NEU: Anzeige der Größe/Breite des Stoffes */}
                    {/* Annahme: Größe ist die Breite des Stoffes in cm */}
                    {product.size > 0 && (
                         <p style={{ 
                            ...styles.detailInfo, 
                            fontWeight: 'bold', 
                            borderBottom: '1px dashed #ddd', 
                            paddingBottom: '10px',
                            marginBottom: '20px',
                        }}>
                            Breite: {product.size} cm
                        </p>
                    )}

                    <h2 style={styles.detailHeading}>Beschreibung</h2>
                    <p style={{ lineHeight: '1.6', marginBottom: '30px' }}>
                        {product.description}
                    </p>

                    {/* Warenkorb-Aktion */}
                    <div>
                        <input
                            type="number"
                            min="1"
                            max={product.stock}
                            value={quantity}
                            onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
                            style={styles.quantityInput}
                            disabled={!isInStock}
                        />

                        {/* Warenkorb-Button */}
                        <button 
                            style={styles.addToCartButton}
                            onClick={handleAddToCart}
                            disabled={!isInStock}
                            onMouseEnter={(e) => e.target.style.backgroundColor = isInStock ? 'var(--farbe-sekundär)' : styles.addToCartButton.backgroundColor} 
                            onMouseLeave={(e) => e.target.style.backgroundColor = isInStock ? 'var(--farbe-akzent)' : styles.addToCartButton.backgroundColor}
                        >
                            {isInStock ? 'In den Warenkorb' : 'Nicht verfügbar'}
                        </button>
                    </div>
                    
                    {/* Lokale Bestätigungsnachricht */}
                    {message && (
                        <div style={styles.messageBox}>
                            {message}
                        </div>
                    )}

                </div>
            </div>
        </div>
    );
};

export default ProductDetail;
