// client/src/App.js
import React from 'react';
// Die Bilder importieren (Pfad anpassen)
import logo from './assets/Logo.jpg'; 
// import banner from './assets/Banner.png'; // Wir benötigen keinen direkten Import, da es im CSS als Hintergrund verwendet wird
import './App.css'; 

function App() {
  return (
    // Die Klasse .App erhält das Banner-Bild als Hintergrund über CSS
    <div className="App">

      {/* 1. Header (Banner-Bild ist jetzt der allgemeine Hintergrund) */}
      <header className="App-header" style={{ 
          backgroundColor: 'var(--farbe-hintergrund-header, transparent)', // Header-Hintergrund sollte transparent sein
          textAlign: 'center', 
          padding: '0' 
      }}>
        {/* Das Banner-Bild (Banner.png) wurde entfernt und wird nun über CSS als Hintergrundbild für .App gesetzt */}
        <h1 style={{ color: 'var(--farbe-schrift, black)', padding: '20px' }}>Henkes-Stoffzauber</h1>
      </header>

      {/* 2. Hauptinhalt der Seite */}
      <main style={{ padding: '20px', textAlign: 'center', backgroundColor: 'rgba(255, 255, 255, 0.8)' }}> 
        {/* Hinzufügen eines semi-transparenten Hintergrunds für 'main', damit der Text auf dem Hintergrundbild besser lesbar ist */}
        <h2>Willkommen bei Henkes-Stoffzauber.de!</h2>

        {/* Das Logo als visuelle Marke in der Mitte */}
        <img 
          src={logo} 
          className="App-logo" 
          alt="Henkes-Stoffzauber Logo" 
          style={{ width: '150px', margin: '20px 0' }}
        />

        <p>Hier entsteht der kreative Ort für Mützen, Schals, Taschen und tolle Nähtipps von Andrea Henke.</p>
        <button>Zum Shop</button>
        <button style={{ marginLeft: '10px' }}>Zum Blog</button>
      </main>
    </div>
  );
}

export default App;
