import React, { useState } from 'react';

// Konstante für das hartkodierte Passwort (NUR FÜR FRONTEND-SCHUTZ)
const ADMIN_PASSWORD = "stoffzauber2025"; 

const AdminLogin = ({ onLogin }) => {
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        
        if (password === ADMIN_PASSWORD) {
            // Erfolgreicher Login: Callback aufrufen, um den Zustand in App.js zu ändern
            onLogin(true);
        } else {
            setError('Falsches Passwort. Bitte versuchen Sie es erneut.');
            setPassword(''); // Passwortfeld leeren
        }
    };

    return (
        <div className="flex justify-center items-center py-10">
            <div className="form-card w-full max-w-md mx-auto">
                <h2 style={{ color: 'var(--farbe-text)', textAlign: 'center', marginBottom: '20px' }}>Admin-Anmeldung</h2>
                
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="password">Passwort:</label>
                        <input
                            id="password"
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    
                    {error && (
                        <p style={{ color: 'var(--farbe-rot)', marginBottom: '15px' }}>
                            {error}
                        </p>
                    )}

                    <button type="submit" style={{ width: '100%', backgroundColor: 'var(--farbe-text)' }}>
                        Anmelden
                    </button>
                </form>
                <p style={{ marginTop: '20px', fontSize: '0.8em', color: 'var(--farbe-text)', opacity: 0.6, textAlign: 'center' }}>
                    Hinweis: Dies ist ein einfacher Frontend-Schutz mit dem Passwort "stoffzauber2025".
                </p>
            </div>
        </div>
    );
};

export default AdminLogin;
