// client/src/App.js

import React, { useState, useEffect } from 'react';
import './App.css'; 

function App() {
  const [produkte, setProdukte] = useState([]);
  const [ladefehler, setLadefehler] = useState(null);

  useEffect(() => {
    // API-Aufruf zum Node.js-Backend (Standard: Port 5000)
    fetch('http://localhost:5000/api/produkte')
      .then(res => {
        if (!res.ok) {
          throw new Error('Netzwerkantwort war nicht ok');
        }
        return res.json();
      })
      .then(daten => setProdukte(daten))
      .catch(error => {
        console.error('Fehler beim Abrufen der Daten:', error);
        setLadefehler(error.message);
      });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸ§µ Henkes-Stoffzauber.de ðŸ§µ</h1>
        <p>Willkommen im React-Shop & Blog!</p>
      </header>
      <main>
        <h2>Produkte vom Node.js-Backend</h2>
        {ladefehler ? (
          <p style={{ color: 'red' }}>Fehler: {ladefehler}. LÃ¤uft der Node.js Server (Port 5000)?</p>
        ) : produkte.length === 0 ? (
          <p>Lade Produkte...</p>
        ) : (
          <div className="produktliste">
            {produkte.map(produkt => (
              <div key={produkt.id} className="produktkarte">
                <h3>{produkt.name}</h3>
                <p>Preis: {produkt.preis.toFixed(2)} â‚¬</p>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
