import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from './CartContext'; 

const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/CCCCCC/333333?text=Stoffmuster";

const getImageUrl = (imagePath) => imagePath?.startsWith('/uploads/') ? `${BACKEND_URL}${imagePath}` : imagePath;

// ==================== ProductCard ====================
const ProductCard = ({ product }) => {
    const { addToCart } = useCart();
    const isInStock = product.stock > 0;

    const handleAddToCart = (e) => {
        e.preventDefault();
        if (isInStock) addToCart(product, 1);
    };

    return (
        <div className="product-card">
            <Link to={`/shop/${product.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                <img 
                    src={getImageUrl(product.imageUrl)} 
                    alt={product.name} 
                    className="product-card-image"
                    onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }}
                />
                <div className="product-card-content">
                    <h3 className="product-card-title">{product.name}</h3>
                    <p className="product-card-price">€ {product.price.toFixed(2)}</p>
                    <p className={`product-card-stock ${isInStock ? 'in-stock' : 'out-of-stock'}`}>
                        {isInStock ? 'Sofort verfügbar' : 'Ausverkauft'}
                    </p>
                    <button 
                        className={`product-card-button ${isInStock ? '' : 'disabled'}`}
                        onClick={handleAddToCart}
                        disabled={!isInStock}
                    >
                        {isInStock ? 'In den Warenkorb (1)' : 'Nicht lieferbar'}
                    </button>
                </div>
            </Link>
        </div>
    );
};

// ==================== Shop ====================
const Shop = () => {
    const [products, setProducts] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const res = await fetch(API_URL);
                if (!res.ok) throw new Error(`Netzwerkfehler: ${res.status}`);
                setProducts(await res.json());
            } catch (e) {
                console.error(e);
                setError(e.message);
            } finally {
                setIsLoading(false);
            }
        };
        fetchProducts();
    }, []);

    if (isLoading) return <p className="centered-text">Shop wird geladen...</p>;
    if (error) return <p className="centered-text error-text">Fehler: {error}</p>;

    return (
        <div className="shop-container">
            <h1 className="shop-title">Alle Stoffe & Zubehör</h1>
            {products.length === 0 ? (
                <p className="centered-text">Der Shop ist momentan leer. Bitte fügen Sie Produkte über das Admin Dashboard hinzu.</p>
            ) : (
                <div className="product-grid">
                    {products.map(product => <ProductCard key={product.id} product={product} />)}
                </div>
            )}
        </div>
    );
};

export default Shop;
