// client/src/components/Stoffe.js
import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';

// WICHTIG: Die URL muss später auf einen funktionierenden Backend-Endpoint angepasst werden.
const API_URL = 'http://localhost:3001/api/fabrics'; 
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/D9E3F0/333?text=Stoffmuster";

/**
 * HILFSFUNKTION: Korrigiert Bildpfade, die vom Backend kommen.
 */
const getImageUrl = (imagePath) => {
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `${BACKEND_URL}${imagePath}`;
    }
    return imagePath || FALLBACK_IMAGE_URL;
};

// Liste der möglichen Verwendungszwecke für die Filter
const USAGE_OPTIONS = [
    'Mütze (Außen)', 'Mütze (Innen)', 'Bündchen', 'Bekleidung (Oberteil)', 
    'Bekleidung (Hose)', 'Babybekleidung', 'Deko', 'Taschen', 'Accessoires'
];


const Stoffe = () => {
    const [stoffe, setStoffe] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedUsage, setSelectedUsage] = useState('Alle'); // Filter
    const [searchTerm, setSearchTerm] = useState(''); // Suchbegriff
    const [sortBy, setSortBy] = useState('name-asc'); // Sortierung

    const navigate = useNavigate();

    // 1. Datenabruf beim Laden der Komponente
    useEffect(() => {
        const fetchStoffe = async () => {
            try {
                const response = await fetch(API_URL);
                if (!response.ok) {
                    throw new Error(`HTTP-Fehler! Status: ${response.status}`);
                }
                const data = await response.json();
                setStoffe(data);
                setError(null);
            } catch (err) {
                console.error("Fehler beim Abrufen der Stoffe:", err);
                setError('Die Stoffdaten konnten nicht geladen werden. Bitte versuche es später erneut.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchStoffe();
    }, []);

    // 2. Filterung und Sortierung der Stoffe
    const filteredAndSortedStoffe = useMemo(() => {
        let currentStoffe = [...stoffe];

        // A) Filterung nach Verwendungszweck
        if (selectedUsage !== 'Alle') {
            currentStoffe = currentStoffe.filter(stoff => 
                // Prüft, ob der Stoff den ausgewählten Verwendungszweck im Array 'suitableFor' enthält.
                // Wir gehen davon aus, dass suitableFor ein Array von Strings ist (z.B. ["Mütze (Außen)", "Bekleidung (Oberteil)"]).
                stoff.suitableFor && stoff.suitableFor.includes(selectedUsage)
            );
        }

        // B) Filterung nach Suchbegriff (Name oder Beschreibung)
        if (searchTerm) {
            const lowerCaseSearchTerm = searchTerm.toLowerCase();
            currentStoffe = currentStoffe.filter(stoff =>
                stoff.name.toLowerCase().includes(lowerCaseSearchTerm) ||
                stoff.description.toLowerCase().includes(lowerCaseSearchTerm)
            );
        }

        // C) Sortierung
        currentStoffe.sort((a, b) => {
            const [field, direction] = sortBy.split('-');
            let aValue = a[field] || '';
            let bValue = b[field] || '';

            // Spezielle Handhabung für Preis, falls vorhanden (hier nicht primär)
            if (field === 'price') {
                aValue = parseFloat(aValue) || 0;
                bValue = parseFloat(bValue) || 0;
            } else if (typeof aValue === 'string') {
                 // Sortiert Strings alphabetisch
                aValue = aValue.toLowerCase();
                bValue = bValue.toLowerCase();
            }

            if (aValue < bValue) return direction === 'asc' ? -1 : 1;
            if (aValue > bValue) return direction === 'asc' ? 1 : -1;
            return 0;
        });

        return currentStoffe;
    }, [stoffe, selectedUsage, searchTerm, sortBy]);


    if (isLoading) {
        return <div className="text-center mt-20 text-xl font-semibold" style={{ color: 'var(--farbe-text)' }}>Stoffkatalog wird geladen...</div>;
    }

    if (error) {
        return <div className="text-center mt-20 text-red-600 font-semibold">{error}</div>;
    }


    return (
        <div className="py-8">
            <h1 className="text-4xl font-extrabold mb-8 text-center" style={{ color: 'var(--farbe-primär)' }}>
                Unser Stoffzauber-Katalog
            </h1>

            {/* Filter- und Sortierleiste */}
            <div className="flex flex-col md:flex-row gap-4 mb-8 p-4 rounded-xl shadow-lg" style={{ backgroundColor: 'var(--farbe-hell)' }}>
                
                {/* 1. Suche */}
                <input
                    type="text"
                    placeholder="Stoffe suchen (Name, Beschreibung...)"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="p-2 border rounded-lg flex-grow"
                    style={{ borderColor: 'var(--farbe-grau)' }}
                />

                {/* 2. Filter nach Verwendungszweck */}
                <select
                    value={selectedUsage}
                    onChange={(e) => setSelectedUsage(e.target.value)}
                    className="p-2 border rounded-lg"
                    style={{ borderColor: 'var(--farbe-grau)' }}
                >
                    <option value="Alle">-- Verwendungszweck (Filter) --</option>
                    {USAGE_OPTIONS.map(usage => (
                        <option key={usage} value={usage}>{usage}</option>
                    ))}
                </select>

                {/* 3. Sortierung */}
                <select
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    className="p-2 border rounded-lg"
                    style={{ borderColor: 'var(--farbe-grau)' }}
                >
                    <option value="name-asc">Alphabetisch (A-Z)</option>
                    <option value="name-desc">Alphabetisch (Z-A)</option>
                    <option value="stock-desc">Bestand (Hoch  Niedrig)</option>
                    <option value="stock-asc">Bestand (Niedrig  Hoch)</option>
                </select>
            </div>


            {/* Stoffe-Liste */}
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                {filteredAndSortedStoffe.length > 0 ? (
                    filteredAndSortedStoffe.map(stoff => (
                        <div 
                            key={stoff.id} 
                            className="stoff-card p-4 rounded-xl shadow-md hover:shadow-xl transition-shadow duration-300 cursor-pointer"
                            style={{ backgroundColor: 'var(--farbe-hell)' }}
                        >
                            <img 
                                src={getImageUrl(stoff.imageUrl)} 
                                alt={stoff.name} 
                                className="w-full h-48 object-cover rounded-lg mb-4" 
                                onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }}
                            />
                            <h2 className="text-xl font-bold mb-2" style={{ color: 'var(--farbe-text)' }}>
                                {stoff.name}
                            </h2>
                            <p className="text-sm mb-4" style={{ color: 'var(--farbe-text-dunkel)' }}>
                                {stoff.description.substring(0, 80)}{stoff.description.length > 80 ? '...' : ''}
                            </p>
                            
                            <p className="font-semibold text-lg" style={{ color: 'var(--farbe-akzent)' }}>
                                Bestand: {stoff.stock || 0} m
                            </p>
                            
                            {/* Verwendungszwecke als Tags anzeigen */}
                            <div className="flex flex-wrap gap-1 mt-2">
                                {stoff.suitableFor && stoff.suitableFor.slice(0, 3).map(usage => (
                                    <span key={usage} className="text-xs px-2 py-1 rounded-full" style={{ backgroundColor: 'var(--farbe-primär-dunkel)', color: 'white' }}>
                                        {usage}
                                    </span>
                                ))}
                                {stoff.suitableFor && stoff.suitableFor.length > 3 && (
                                    <span className="text-xs px-2 py-1 rounded-full" style={{ backgroundColor: 'var(--farbe-grau)', color: 'var(--farbe-text)' }}>
                                        +{stoff.suitableFor.length - 3}
                                    </span>
                                )}
                            </div>
                        </div>
                    ))
                ) : (
                    <p className="col-span-full text-center text-lg mt-10" style={{ color: 'var(--farbe-text-dunkel)' }}>
                        Keine Stoffe gefunden, die den Kriterien entsprechen.
                    </p>
                )}
            </div>
        </div>
    );
};

export default Stoffe;
